import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFaceKiss } from '@fortawesome/free-solid-svg-icons';
import { Card, Modal } from 'antd';
import { useState } from 'react';
import { useSelector } from 'react-redux';

const Followings = () => {

  const {me} = useSelector((state) => state.user);

  const [isModalOpen, setIsModalOpen] = useState(false);


  const referModal = () => {
    setIsModalOpen(true);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const IfFollowing = () => {

    if(me.Liked[0] !== undefined){
      return <li><FontAwesomeIcon icon={faFaceKiss} style={{fontSize:15, color: 'pink'}} /> {me.Followings[0].nickname}</li>;
    }
    if(me.Liked[1] !== undefined){
      return <li><FontAwesomeIcon icon={faFaceKiss} style={{fontSize:15, color: 'pink'}} />{me.Followings[1].nickname}</li>;
    }
    if(me.Liked[2] !== undefined){
      return <li><FontAwesomeIcon icon={faFaceKiss} style={{fontSize:15, color: 'pink'}} />{me.Followings[2].nickname}</li>;
    }
    else{
      return  <div></div>;
    }

};

const originalImage = `https://static.vecteezy.com/system/resources/previews/002/318/271/original/user-profile-icon-free-vector.jpg`;

    return(
        <div className="site-card-border-less-wrapper">
        <Card
          title="Followings"
          style={{width: 300, marginLeft: 20, height: 215}}
          extra={<a onClick={referModal}>More</a>}
        >
          <p></p>

    </Card>
    <Modal title={me.nickname + " 의 팔로잉 목록"} footer={null} onCancel={handleCancel} open={isModalOpen}>
          {me.Followings.map((following) => (
          <ul key={following.id}>
            <p><FontAwesomeIcon icon={faFaceKiss} style={{fontSize:15, color: 'pink'}} /> 
            <img style={{width: 60}} src={following.Image.src === originalImage ? originalImage : `http://localhost:3065/${following.Image.src}`} />
            </p>
          </ul>
          ))}
        </Modal>
      </div>
    );

};

export default Followings;
