import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { Card, Modal } from 'antd';
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import wrapper from '../../store/configureStore';
import axios from 'axios';
import { END } from 'redux-saga';
import { LOAD_USER_REQUEST } from '../../reducers/user';

const UserLikeList = () => {

  const {userInfo} = useSelector((state) => state.user);

  const [isModalOpen, setIsModalOpen] = useState(false);


  console.log("유저인포", userInfo);

  const referModal = () => {
    setIsModalOpen(true);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const IfLike = () => {

      if(userInfo.Liked[0] !== undefined){
        return <li><FontAwesomeIcon icon={faHeart} style={{fontSize:15, color: 'pink'}} /> {userInfo.Liked[0].lookName}</li>;
      }
      if(userInfo.Liked[1] !== undefined){
        return <li><FontAwesomeIcon icon={faHeart} style={{fontSize:15, color: 'pink'}} />{userInfo.Liked[1].lookName}</li>;
      }
      if(userInfo.Liked[2] !== undefined){
        return <li><FontAwesomeIcon icon={faHeart} style={{fontSize:15, color: 'pink'}} />{userInfo.Liked[2].lookName}</li>;
      }
      else{
        return  <div></div>;
      }

  };


    return(
        <div className="site-card-border-less-wrapper">
        <Card
          title="I like this!"
          style={{width: 300, height: 215}}
          extra={<a onClick={referModal}>More</a>}
        >
          <IfLike />
    </Card>
    <Modal title={userInfo.nickname + " 의 좋아요 목록"} footer={null} onCancel={handleCancel} open={isModalOpen}>
      {userInfo.Liked.map((love) => (
        <ul key={love.id}>
          <p><FontAwesomeIcon icon={faHeart} style={{fontSize:15, color: 'pink'}} /> <img style={{width: 60}} src={`http://localhost:3065/${love.Images[0].src}`} /></p>
        </ul>
      ))}
    </Modal>
      </div>
    );

};

