import React from "react";
import { useSelector } from "react-redux";
import moment from "moment";
import Link from 'next/link';

const DateCellRender = () => {

   const {mainPosts} = useSelector((state) => state.post);

   console.log("DateCellRender:::::::::" + JSON.stringify(mainPosts));
 //  console.log("Render:::::::::" + JSON.stringify(mainPosts[0].Images[0].src));

   const getDayInfo = () => {
      let calendarData;
      let diaryMoment;
      for( let i = 0; i < mainPosts.length; i++ ){
         console.log("생성 날짜",  moment(mainPosts[0].createdAt).format("YYYY-MM-DD"));
         diaryMoment = moment(mainPosts[i].createdAt).format("YYYY-MM-DD");
       //  console.log("diaryMoment:::::" + diaryMoment);
       const value = moment;
         if(diaryMoment === value.format("YYYY-MM-DD")){
          //  console.log("if문 실행");
            calendarData = [
               {
                  photo: mainPosts[i].Images[0].src,
                  title: mainPosts[i].lookName

               }
            ];
         }
      }
      console.log("photo:::::" + JSON.stringify(calendarData));
      return calendarData || [];
   };

   const dayInfo = getDayInfo();

  //const photo = `http://localhost:3065/sean-kong-1dJmkdVuelg-unsplash_1669727155305.jpg`;

 return(
   //  <img src={photo} style={{width:50}} />
  <>
   {dayInfo.map((info) => (
      <div>
         
         <h5>{info.title}</h5>
         <Link href="#">
         <img style={{width: 50}} src={`http://localhost:3065/${info.photo}`} />
         </Link>
      </div>
      
   ))}
  </>
 );
};

export default DateCellRender;
