import { Button, Card, Col, Input, Row, Select, Space, Switch, Upload  } from 'antd';
import { EditOutlined, UploadOutlined  } from '@ant-design/icons';
import TextArea from "antd/lib/input/TextArea";
import AppLayout from '../../components/AppLayout';
import { LOAD_MY_INFO_REQUEST } from '../../reducers/user';
import { LOAD_POST_REQUEST } from '../../reducers/post';
import wrapper from '../../store/configureStore';
import { END } from 'redux-saga';
import { useRouter } from 'next/router';
import { useSelector } from 'react-redux';
import axios from 'axios';
import Look from '../look';
import NewComment from '../../components/newComment';
import { useEffect, useState } from 'react';
import Comments from '../../components/comments';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import NoComments from '../../components/noComments';
const { Meta } = Card;
const { Option } = Select;

const Post = () => {

    const router = useRouter();
    const {id} = router.query;
    const {userCalendarData, me} = useSelector((state) => state.user);

    const [index, setIndex] = useState(true);

    console.log("singlePost::::: ", userCalendarData);

    const onSelect = () => {
        setIndex(!index);
        console.log("index: ", index);
      };

    return(
        <AppLayout >
        <div style={{width: 1120}}>
        <h1>DailyLook</h1>
            <Col style={{float: 'left'}}>
                <Card
                style={{
                width: 280,
                
                }}
                cover={<img style={{height: 550}} alt="example" src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png" />}
                actions={[
                    
                    <EditOutlined key="edit" />,
                    <Upload>
                        <UploadOutlined />
                    </Upload>
                ]}
              
                >      
                </Card>
                <h1>This look is...{userCalendarData.rateAverage}points.</h1>
            </Col>
        <Col style={{float: 'left', width:500}}>
        <div style={{ }}>
            <ul>
            <li style={{display: 'inline-block'}}><h3 >데일리룩 제목</h3></li>
            <li style={{display: 'inline-block', marginLeft:10}}><Input defaultValue={userCalendarData.lookName} readOnly /></li>
            </ul>
            
            {/* <ul>
            <li style={{display: 'inline-block'}}><h3 >카테고리</h3></li>
            <li style={{display: 'inline-block', marginLeft:18}}>
            <Select
                defaultValue="카테고리를 선택하세요."
                style={{
                width: 200,
                marginLeft: 30
                }}
            >
                <Option value="jack">Jack</Option>
                <Option value="lucy">Lucy</Option>
            </Select>
            </li>
            </ul> */}
            </div>
            <div>
            <Card style={{marginLeft: 15, marginTop: 0,width:400}}>
            <Button style={{backgroundColor: 'black', color: 'white', marginBottom: 10}} block >Information</Button>
<thead>
    <tr >
    <th>분류</th>
    <th>브랜드명</th>
    
    </tr>
</thead>
<tbody style={{textAlign:'center'}} >
    <tr>
    <td> <Input disabled  defaultValue="상의" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input defaultValue={userCalendarData.top} readOnly  /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="하의" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input  defaultValue={userCalendarData.bottom} readOnly /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="원피스" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input  defaultValue={userCalendarData.dress} readOnly /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="아우터" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input  defaultValue={userCalendarData.outer} readOnly /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="신발" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input defaultValue={userCalendarData.shoes} readOnly  /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="패션잡화" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input defaultValue={userCalendarData.acc} readOnly /></td> 
    </tr>

</tbody>
</Card>
</div>
<Card style={{ marginTop: 25, marginLeft: 15, width: 400, height: 210}}>
<Button style={{backgroundColor: 'black', color: 'white', marginBottom: 10}} block >Daily Record </Button>
<TextArea
  showCount
  maxLength={250}
  defaultValue={singlePost.content} 
  readOnly
  style={{
    height: 110,
    resize: 'none',
  }}
/>
</Card>

<Button 
style={{ left: '20%', marginTop: 5, backgroundColor: 'black', color: 'white'}}
>
    삭제하기
</Button>
<Button
style={{ left: '30%', marginTop: 5, backgroundColor: 'black', color: 'white'}}
>수정하기</Button>
</Col>

<Col>
{/* 댓글 */}
<Switch defaultChecked={index} onChange={onSelect} style={{marginLeft: 233}} checkedChildren="댓글쓰기" unCheckedChildren="댓글보기" />
{index === true  ? <NewComment post={singlePost} /> : null }
{index === false && userCalendarData.Comments !== [] ? <Comments post={userCalendarData} /> : null }
</Col>

</div>
    </AppLayout>
    );

};

export const getServerSideProps = wrapper.getServerSideProps(async(context) => { 
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = ''; 
    if(context.req && cookie){ 
        axios.defaults.headers.Cookie = cookie;
    }
   context.store.dispatch({
        type: LOAD_MY_INFO_REQUEST
    });
    context.store.dispatch({
        type: LOAD_POST_REQUEST,
        data: context.params.id //혹은 context.query.id
    });
   context.store.dispatch(END);
   await context.store.sagaTask.toPromise();
});


export default Post;
