import React, { useCallback, useRef, useState } from "react";
import '../pages/css/myInfo.module.css';
import { Button, Card } from 'antd';
import { EditOutlined } from '@ant-design/icons';
const { Meta } = Card;

const MyInfo = () => {

  const [previewImg, setPreviewImg] = useState(null);
  const orgFile = useRef(); 
  const insertImg = (e) => {
    e.preventDefault();
    const file = e.target.files[0];
    setImageSrc([...imageSrc, { uploadedFile: file }]);

    let reader = new FileReader();
    if(e.target.files[0]){
      reader.readAsDataURL(e.target.files[0])
    }
    reader.onloadend = () => {
      const previewImgUrl = reader.result
      if(previewImgUrl){
        setPreviewImg(previewImgUrl)
      }
    }
  }

  const handleClick = useCallback(() => {
    orgFile.current.click();
}, [orgFile.current]);


    return(
        <div>
      <Card
        title="My Info"
        style={{marginLeft: 15, width: 300}}
        cover={
          <img
            alt="example"
            src={previewImg ? previewImg : 'https://static.vecteezy.com/system/resources/previews/002/318/271/original/user-profile-icon-free-vector.jpg'}
            height='250px'
          />
              }
    actions={[
      <Button 
      type="file"
      onClick={handleClick}
      ref={orgFile}
      onChange={(e) => {insertImg(e);}}
      ><EditOutlined key="edit" /></Button>,
    ]}
  >
    <Meta
      title="Self Introductions"
      description="This is the description"
    />
  </Card>
        </div>
    );

};

export default MyInfo;
