import { Button, Card, Col, Form, Input, Modal, Row, Select, Space, Switch, Tooltip, Upload  } from 'antd';
import { EditOutlined, HeartOutlined, HeartTwoTone, UploadOutlined  } from '@ant-design/icons';
import TextArea from "antd/lib/input/TextArea";
import AppLayout from '../../components/AppLayout';
import { FOLLOW_REQUEST, LOAD_MY_INFO_REQUEST, UNFOLLOW_REQUEST } from '../../reducers/user';
import { LIKE_POST_REQUEST, LOAD_POST_REQUEST, RETWEET_REQUEST, UNLIKE_POST_REQUEST } from '../../reducers/post';
import wrapper from '../../store/configureStore';
import { END } from 'redux-saga';
import { useRouter } from 'next/router';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import Look from '../look';
import NewComment from '../../components/newComment';
import { useCallback, useEffect, useState } from 'react';
import Comments from '../../components/comments';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faPenToSquare} from "@fortawesome/free-regular-svg-icons";
import {faHeartPulse} from "@fortawesome/free-solid-svg-icons";
import { faHouse } from '@fortawesome/free-solid-svg-icons';
import NoComments from '../../components/noComments';
import '../css/post[id].module.css';
import FollowButton from '../../components/FollowButton';
import useInput from '../../hooks/useInput';
const { Meta } = Card;
const { Option } = Select;
import styled from 'styled-components';

const One = styled.div`

    .div1{
        width: 1120px;
        position: relative;
        z-index: 1;
    }
    .switch1{
        margin-left: 900px;
        margin-top: 36px;
    }
    .icon1{
        position: absolute;
        z-index: 5;
        font-size: 30px;
        margin-left: 200px;
        margin-top: 10px;
    }
    .icon2{
        position: absolute;
        z-index: 5;
        font-size: 30px;
        margin-left: 243px;
        margin-top: 10px;
    }
    .icon3{
        z-index:10;
    }
    .img1{
        margin-left:120px;
        margin-top: 20px;
        height: 330px;
    }
    .img2{
        height: 550px;
    }
    .button1{
        margin-left: 384px;
    }
    .button2{
        background-color: black;
        color: white;
        margin-bottom: 10px;
    }
    
    .col1{
        float: left;
    }
    .card1{
        width: 280px;
        z-index: 1;
    }
    .card2{
        margin-left: 15px;
        width:400px;
    }
    .li1{
        display: inline-block;
    }
    .li2{
        display: ;inline-block;
        margin-left:10px;
    }
    
`;

const Post = () => {

 
    const dispatch = useDispatch();

   


    const router = useRouter();
    const {id} = router.query;
    const {userCalendarData, me, followLoading, unfollowLoading} = useSelector((state) => state.user);
    const {singlePost, retweetError, retweetDone} = useSelector((state) => state.post);

    useEffect(() => {
        dispatch({
            type: LOAD_POST_REQUEST,
            data: id
        });
    }, []);


    const [index, setIndex] = useState(true);

    const [isModalOpen, setIsModalOpen] = useState(false);

    const [content, onChangeContent] = useInput('');

    console.log("singlePost::::: ", singlePost);

    const liked = singlePost.Likers[0];

    const isFollowing = me?.Followings.find((v) => v.id === singlePost.UserId);

    console.log("좋아요: ", liked);

    console.log("텍스트: ", content);

    const tags = [];

    for(let i = 0; i < singlePost.Hashtags.length; i++){
        
        if(singlePost.Hashtags !== undefined){
            tags.push( "#" + singlePost.Hashtags[i].name );
        }
    }

    console.log("해시태그: ", tags);

    //댓글 쓰기, 보기 선택
    const onSelect = () => {
        setIndex(!index);
        console.log("index: ", index);
      };

      //리트윗 에러
      useEffect(() => {
        if(retweetError){
            window.alert(retweetError);
        }
      },[retweetError]);

      //좋아요
      const onLike = useCallback(() => {
        if(!id){
            return alert('로그인이 필요합니다.');
        }
       return dispatch({
                type: LIKE_POST_REQUEST,
                data: id
            });
        },[id]);

        //좋아요 취소
        const onUnlike = useCallback(() => {
            if(!id){
                return alert('로그인이 필요합니다.');
            }
            return dispatch({
            type: UNLIKE_POST_REQUEST,
            data: id
           });
        },[id]);

        //팔로우
        const onClickButton = useCallback(() => {
            if(isFollowing){
                dispatch({
                    type: UNFOLLOW_REQUEST,
                    data: singlePost.UserId
                }, window.alert("언팔로우 합니다."));
            }
            else{
                dispatch({
                    type: FOLLOW_REQUEST,
                    data: singlePost.UserId
                }, window.alert("팔로우 합니다."));
            }
        },[isFollowing]);

        //리트윗모달
        const retweetModal = () => {
            if(!id){
                return alert('로그인이 필요합니다.');
            }
            setIsModalOpen(true);
        };

        //리트윗
        const retweetSubmit = useCallback(() => {
            const formData = new FormData();

            formData.append('content', content);
            formData.append('postId', singlePost.id);

            dispatch({
                type: RETWEET_REQUEST,
                data: formData
            },
            router.reload());
        }, [content]);
        
        //리트윗 모달 창 닫기
        const handleCancel = () => {
            setIsModalOpen(false);
          };

    //댓글 없을 때
    const IfComments = () => {
        if(index === false){
            if(singlePost.Comments.length === 0){
                return (
                    <NoComments />
                );
            }else{
                <Comments post={singlePost} />
            }
        }else{
            return(
                null
            );
        }
    };


    return(
        <AppLayout >
            <One>
        <div className='div1'>
       <Row>
        <h1>DailyLook</h1>
        <Switch defaultChecked={index} onChange={onSelect} className='switch1' checkedChildren="댓글쓰기" unCheckedChildren="댓글보기" />
       </Row>
       {me.id === singlePost.UserId 
            ?
            null
            :
            <Tooltip title="내 계정에 인용하기">
            <FontAwesomeIcon 
                    className='icon1'
                    icon={faPenToSquare} 
                    onClick={retweetModal}
            
            />
            </Tooltip>
       }
      
            <Modal title="인용하기" footer={null} onCancel={handleCancel} open={isModalOpen} >
                <Form  onFinish={retweetSubmit}>
                    <Input.TextArea 
                    value={content}
                    onChange={onChangeContent}
                    maxLength={200}
                    placeholder="인용 메모를 남겨보세요."
                    />
                    <img className='img1' src={`http://localhost:3065/${singlePost.Images[0].src}`} /> 
                    <Button className='button1' htmlType="submit">인용하기</Button>
                </Form>
            </Modal>

        {liked ? 
        <Tooltip title="좋아요 취소">
        <HeartTwoTone onClick={onUnlike} twoToneColor="#fc83c6" className='icon2'/> 
        </Tooltip>
        :
        <Tooltip title="좋아요">
        <HeartOutlined onClick={onLike} className='icon2' /> 
        </Tooltip>
        }

            <Col className='col1' >
                
                <Card
                className='card1'
                
                cover={<One><img className='img2' alt="example" src={`http://localhost:3065/${singlePost.Images[0].src}`} /></One>}
                actions={[
                    id && singlePost.UserId === id
                    ? 
                    [
                    <EditOutlined key="edit" />,
                    <Upload>
                        <UploadOutlined />
                    </Upload>
                    ]
                    :
                    <One>
                    <Tooltip title={isFollowing ? '언팔로우' : '팔로우'}>
                        <FontAwesomeIcon
                        icon={faHeartPulse}
                        fontSize={20}
                        onClick={onClickButton}
                        className='icon3'
                        />
                    </Tooltip>,
                    <Tooltip title={singlePost.User.nickname + "의 홈 방문하기"}>
                        <a href={`http://localhost:3000/user/${singlePost.UserId}`}>
                        <FontAwesomeIcon
                         icon={faHouse} 
                         fontSize={20}
                         className='icon3'
                         />
                        </a>
                    </Tooltip>
                    </One>
                    
                ]}
                >      
                <Meta title={"글쓴이:  " + singlePost.User.nickname} />
                </Card>
                <h1>This look is...{singlePost.rateAverage}points.</h1>
            </Col>
        <Col style={{float: 'left', width:500}}>
        <div>
            <ul>
            <li className='li1'><h3 >데일리룩 제목</h3></li>
            <li className='li2'><Input defaultValue={singlePost.lookName} readOnly /></li>
            </ul>

            </div>
            <div>
            <Card className='card2'>
            <Button className='button2' block >Information</Button>
<thead>
    <tr >
    <th>분류</th>
    <th>브랜드명</th>
    
    </tr>
</thead>
<tbody style={{textAlign:'center'}} >
    <tr>
    <td> <Input disabled  defaultValue="상의" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input defaultValue={singlePost.top} readOnly  /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="하의" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input  defaultValue={singlePost.bottom} readOnly /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="원피스" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input  defaultValue={singlePost.dress} readOnly /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="아우터" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input  defaultValue={singlePost.outer} readOnly /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="신발" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input defaultValue={singlePost.shoes} readOnly  /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="패션잡화" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input defaultValue={singlePost.acc} readOnly /></td> 
    </tr>

</tbody>
</Card>
</div>
<Card style={{ marginTop: 25, marginLeft: 15, width: 400, height: 210}}>
<Button style={{backgroundColor: 'black', color: 'white', marginBottom: 10}} block >Daily Record </Button>
<TextArea
  showCount
  maxLength={250}
  defaultValue={singlePost.content + '\n\n\n' + tags} 
  readOnly
  style={{
    height: 110,
    resize: 'none',
  }}
/>
</Card>
    { id && singlePost.UserId === id
    ?
        (
            <>
            <Button 
            style={{ left: '20%', marginTop: 5, backgroundColor: 'black', color: 'white'}}>
                삭제하기
            </Button>
            <Button
            style={{ left: '30%', marginTop: 5, backgroundColor: 'black', color: 'white'}}>
                수정하기
            </Button>
            </>
        )
        :
        null
    }



</Col>

<Col>
    {/* 댓글 */}
    {index === true  ? <NewComment post={singlePost} /> : null }
    {index === false  ? ( singlePost.Comments.length !== 0 ? <Comments post={singlePost} /> : <NoComments />) : null }
    {/* <IfComments /> */}
</Col>

</div>
</One>
    </AppLayout>
    );

};

export const getServerSideProps = wrapper.getServerSideProps(async(context) => { 
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = ''; 
    if(context.req && cookie){ 
        axios.defaults.headers.Cookie = cookie;
    }
   context.store.dispatch({
        type: LOAD_MY_INFO_REQUEST
    });
    context.store.dispatch({
        type: LOAD_POST_REQUEST,
        data: context.params.id //혹은 context.query.id
    });
   context.store.dispatch(END);
   await context.store.sagaTask.toPromise();
});


export default Post;
