import { Card, Empty, List, Modal } from 'antd';
import { EditOutlined } from '@ant-design/icons';
import { useSelector } from 'react-redux';
import { useState } from 'react';
import { useRouter } from 'next/router';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPenNib } from '@fortawesome/free-solid-svg-icons';
const { Meta } = Card;
import styled from 'styled-components';

const One = styled.div`
  .card1{
    width: 250px;
    height: 215px;
    float: 'left';
    margin-left: 17px;
    margin-top: 30px;
  }
`;


const Mention = () => {

  const {me} = useSelector((state) => state.user);

  const router = useRouter();
  const {id} = router.query;

  const [isModalOpen, setIsModalOpen] = useState(false);

  console.log("인용리스트", me);

  const referModal = () => {
    setIsModalOpen(true);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const IfRefer = () => {

    // for(let i = 0; i < me.References.length; i++){
    //   return( <li>{me.References[i].content}</li>);
    // }

      if(me.References[0] !== undefined){
        return <li><FontAwesomeIcon icon={faPenNib} style={{fontSize: 15}} />{me.References[0].content}</li>;
      }
      if(me.References[1] !== undefined){
        return <li><FontAwesomeIcon icon={faPenNib} style={{fontSize: 15}} />{me.References[1].content}</li>;
      }
      if(me.References[2] !== undefined){
        return <li><FontAwesomeIcon icon={faPenNib} style={{fontSize: 15}} />{me.References[2].content}</li>;
      }
      else{
        return  <div>
                  <Empty
                  description={
                    <span>
                      아직 인용이 없어요.
                    </span>
                  } 
                  />
                </div>;
      }
  

  };

    return(
        <One>
            <Card
            title="I refered..."
            extra={<a onClick={referModal}>More</a>}
            >
              <IfRefer />
            </Card>
            <Modal title={me.nickname + " 의 인용 목록"} footer={null} onCancel={handleCancel} open={isModalOpen} >
              

            <List
               grid={{
                gutter: 50,
                column: 3
            }}
              dataSource={me.References}
              renderItem={(item) => (
                <List.Item>
                    <Card
                    // title={<a href={`http://localhost:3000/post/${item.id}`} >{item.lookName}</a>}
                    cover={<a href={`http://localhost:3000/post/${item.id}`} ><img style={{width: '100%'}} alt="example" src={`http://localhost:3065/${item.referSrc}`} /></a>}
                    style={{width:110}}
                    >
                      <Meta description={item.content} />
                    </Card>
                </List.Item>
            )}
            >
            </List>
            </Modal>
        </One>
    );
};

// {me.References.map((refer) => (
//   <ul key={refer.id}>
//     <p><FontAwesomeIcon icon={faPenNib} style={{fontSize: 20}} /> <img style={{width: 60}} src={`http://localhost:3065/${refer.referSrc}`}/> {refer.content}</p>
//   </ul>
// ))}

export default Mention;
