import { Button, Card, Col, Form, Input, Modal, Row, Select, Space, Switch, Tooltip, Upload  } from 'antd';
import { EditOutlined, HeartOutlined, HeartTwoTone, UploadOutlined  } from '@ant-design/icons';
import TextArea from "antd/lib/input/TextArea";
import AppLayout from '../../components/AppLayout';
import { FOLLOW_REQUEST, LOAD_MY_INFO_REQUEST, UNFOLLOW_REQUEST } from '../../reducers/user';
import { LIKE_POST_REQUEST, LOAD_POST_REQUEST, RETWEET_REQUEST, UNLIKE_POST_REQUEST, UPDATE_POST_REQUEST, UPLOAD_UPDATE_LOOK_IMAGE_REQUEST } from '../../reducers/post';
import wrapper from '../../store/configureStore';
import { END } from 'redux-saga';
import { Router, useRouter } from 'next/router';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import Look from '../look';
import NewComment from '../../components/newComment';
import { useCallback, useEffect, useRef, useState } from 'react';
import Comments from '../../components/comments';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faPenToSquare} from "@fortawesome/free-regular-svg-icons";
import {faHeartPulse} from "@fortawesome/free-solid-svg-icons";
import { faHeartCrack } from '@fortawesome/free-solid-svg-icons';
import { faHouse } from '@fortawesome/free-solid-svg-icons';
import NoComments from '../../components/noComments';
import '../css/post[id].module.css';
import useInput from '../../hooks/useInput';
const { Meta } = Card;
const { Option } = Select;
import styled from 'styled-components';
import patchInput from '../../hooks/patchInput';
import useSWR from 'swr';
const OneLook = styled.div`

    .div1{
        width: 1120px;
        position: relative;
        z-index: 1;
    }
    .switch1{
        margin-left: 900px;
        margin-top: 36px;
    }
    .icon1{
        position: absolute;
        z-index: 5;
        font-size: 30px;
        margin-left: 200px;
        margin-top: 10px;
    }
    .icon2{
        position: absolute;
        z-index: 5;
        font-size: 30px;
        margin-left: 243px;
        margin-top: 10px;
    }
    .icon3{
        z-index:10;
    }
    .img1{
        margin-left: 120px;
        margin-top: 20px;
        height: 330px;
    }
    .img2{
        height: 550px;
        width: 100%;
    }
    .button1{
        margin-left: 384px;
    }
    .button2{
        background-color: black;
        color: white;
        margin-bottom: 10px;
    }
    .button3{
        background-color: black;
        color: white;
        margin-bottom: 10px;
    }
    .button4{
        margin-left: 20%;
        margin-top: 5px; 
        background-color: black;
        color: white;
    }
    .button5{
       margin-left: 10%;
        margin-top: 5px;
        background-color: black;
        color: white;
    }
    .col1{
        float: left;
    }
    .card1{
        width: 280px;
        z-index: 1;
    }
    .card2{
        margin-left: 15px;
        width: 400px;
    }
    .card3{
        margin-top: 24px; 
        margin-left: 15px;
        width: 400px;
        height: 245px;
    }
    .li1{
        display: inline-block;
    }
    .li2{
        display: inline-block;
        margin-left: 10px;
    }
    .body1{
        text-align: center;
    }
    .input1{
        color: black;
        text-align: center;
    }
    .textArea1{
        height: 110px;
        resize: none;
        margin-top: 20px;
    }
`;

const fetcher = (url) => axios.get(url, {withCredentials: true}).then((result) => result.data);

const Post = () => {


 
    const dispatch = useDispatch();
    const router = useRouter();
    const {id} = router.query;
    const {userCalendarData, followLoading, unfollowLoading} = useSelector((state) => state.user);
    const {imagePaths, retweetError, retweetDone, updatePostDone} = useSelector((state) => state.post);
    const [read, setRead] = useState(true);
    const [previewImg, setPreviewImg] = useState(null);

    const {data: singlePost, error: singlePostError} = useSWR(`http://localhost:3065/post/${id}`, fetcher);
    const {data: me, error} = useSWR(`http://localhost:3065/user`, fetcher);

    console.log(singlePost, me);

    useEffect(() => {
        if(me.id === singlePost.UserId){
            setRead(false);
        }

    }, [me && me.id]);


    const [content, setContent] = useState(singlePost.content);

    const onChangeContent = useCallback((e) => {
        const value = e.target.value;
        setContent((content) => {
          if (typeof value === 'object') {
            return JSON.stringify(value);
          } else {
            return value;
          }
        });
      }, []);

    const [top, setTop] = useState(singlePost.top);

    const onChangeTop = useCallback((e) => {
        const value = e.target.value;
        setTop((top) => {
          if (typeof value === 'object') {
            return JSON.stringify(value);
          } else {
            return value;
          }
        });
      }, []);

      const [bottom, setBottom] = useState(singlePost.bottom);
    
      const onChangeBottom = useCallback((e) => {
          const value = e.target.value;
          setBottom((bottom) => {
            if (typeof value === 'object') {
              return JSON.stringify(value);
            } else {
              return value;
            }
          });
        }, []);

        const [dress, setDress] = useState(singlePost.dress);
    
        const onChangeDress = useCallback((e) => {
            const value = e.target.value;
            setDress((dress) => {
              if (typeof value === 'object') {
                return JSON.stringify(value);
              } else {
                return value;
              }
            });
          }, []);

          const [shoes, setShoes] = useState(singlePost.shoes);
    
          const onChangeShoes = useCallback((e) => {
              const value = e.target.value;
              setShoes((shoes) => {
                if (typeof value === 'object') {
                  return JSON.stringify(value);
                } else {
                  return value;
                }
              });
            }, []);

            const [acc, setAcc] = useState(singlePost.acc);
    
            const onChangeAcc = useCallback((e) => {
                const value = e.target.value;
                setAcc((acc) => {
                  if (typeof value === 'object') {
                    return JSON.stringify(value);
                  } else {
                    return value;
                  }
                });
              }, []);

              const [outer, setOuter] = useState(singlePost.outer);
    
              const onChangeOuter = useCallback((e) => {
                  const value = e.target.value;
                  setOuter((outer) => {
                    if (typeof value === 'object') {
                      return JSON.stringify(value);
                    } else {
                      return value;
                    }
                  });
                }, []);

                const [lookName, setLookName] = useState(singlePost.lookName);
    
                const onChangeLookName = useCallback((e) => {
                    const value = e.target.value;
                    setLookName((lookName) => {
                      if (typeof value === 'object') {
                        return JSON.stringify(value);
                      } else {
                        return value;
                      }
                    });
                  }, []);

    const imageInput = useRef();



    const [index, setIndex] = useState(true);

    const [isModalOpen, setIsModalOpen] = useState(false);

    console.log("singlePost::::: ", singlePost);

    console.log("me::::::", me);

    const liked = singlePost?.Likers[0];

    const isFollowing = me?.Followings.find((v) => v.id === singlePost.UserId);

    console.log("좋아요: ", liked);

    const tags = [];

    //해시태그 #붙여서 뿌리기
    for(let i = 0; i < singlePost.Hashtags.length; i++){
        
        if(singlePost.Hashtags !== undefined){
            tags.push( "#" + singlePost.Hashtags[i].name );
        }
    }

    console.log("해시태그: ", tags);

    //댓글 쓰기, 보기 선택
    const onSelect = () => {
        setIndex(!index);
        console.log("index: ", index);
      };

      //리트윗 에러
      useEffect(() => {
        if(retweetError){
            window.alert(retweetError);
        }
      },[retweetError]);

      //좋아요
      const onLike = useCallback(() => {
        if(!id){
            return alert('로그인이 필요합니다.');
        }
       return dispatch({
                type: LIKE_POST_REQUEST,
                data: id
            });
        },[id]);

        //좋아요 취소
        const onUnlike = useCallback(() => {
            if(!id){
                return alert('로그인이 필요합니다.');
            }
            return dispatch({
            type: UNLIKE_POST_REQUEST,
            data: id
           });
        },[id]);

        //팔로우
        const onClickButton = useCallback(() => {
            if(isFollowing){
                dispatch({
                    type: UNFOLLOW_REQUEST,
                    data: singlePost.UserId
                }, window.alert("언팔로우 합니다."));
            }
            else{
                dispatch({
                    type: FOLLOW_REQUEST,
                    data: singlePost.UserId
                }, window.alert("팔로우 합니다."));
            }
        },[isFollowing]);

        //리트윗모달
        const retweetModal = () => {
            if(!id){
                return alert('로그인이 필요합니다.');
            }
            setIsModalOpen(true);
        };

        //리트윗
        const retweetSubmit = useCallback(() => {
            const formData = new FormData();

            formData.append('content', content);
            formData.append('postId', singlePost.id);

            dispatch({
                type: RETWEET_REQUEST,
                data: formData
            },
            router.reload());
        }, [content]);
        
        //리트윗 모달 창 닫기
        const handleCancel = () => {
            setIsModalOpen(false);
          };

    //댓글 없을 때
    const IfComments = () => {
        if(index === false){
            if(singlePost.Comments.length === 0){
                return (
                    <NoComments />
                );
            }else{
                <Comments post={singlePost} />
            }
        }else{
            return(
                null
            );
        }
    };

    const onClickImageUpload = useCallback(() => {
        imageInput.current.click();
    }, [imageInput.current]);


//게시글 수정 요청
    const onSubmit = useCallback(() => {

        const formData = new FormData();
        imagePaths.forEach((p) => {
            formData.append('image', p);
        });
        formData.append('content', content);
        formData.append('top', top);
        formData.append('bottom', bottom);
        formData.append('dress', dress);
        formData.append('shoes', shoes);
        formData.append('acc', acc);
        formData.append('outer', outer);
        formData.append('lookName', lookName);
        formData.append('postId', id);
        dispatch({
            type: UPDATE_POST_REQUEST,
            data: formData,
         });
        
    },[content, imagePaths]);

    //이미지 미리보기
    const onChangeImages = useCallback((e) => {
    
        const reader = new FileReader();
        if(e.target.files[0]){
          reader.readAsDataURL(e.target.files[0]);
        }
        reader.onloadend = () => {
          const previewImgUrl = reader.result;
          if(previewImgUrl){
            setPreviewImg(previewImgUrl);
          }
        }
        const imageFormData = new FormData();
   
        [].forEach.call(e.target.files, (f) => {
          imageFormData.append('image', f);
      });
    
       return dispatch({
            type: UPLOAD_UPDATE_LOOK_IMAGE_REQUEST,
            data: imageFormData
        });

    });


    return(
        <AppLayout >
            <OneLook>
            <Form encType="multipart/form-data" onFinish={onSubmit}>
        <div className='div1'>
       <Row>
        <h1>DailyLook</h1>
        <Switch defaultChecked={index} onChange={onSelect} className='switch1' checkedChildren="댓글쓰기" unCheckedChildren="댓글보기" />
       </Row>
       {me.id === singlePost.UserId 
            ?
            null
            :
            <OneLook>
            <Tooltip title="내 계정에 인용하기">
            <FontAwesomeIcon 
                    className='icon1'
                    icon={faPenToSquare} 
                    onClick={retweetModal}
            
            />
            </Tooltip>
            </OneLook>
       }
      
            <Modal title="인용하기" footer={null} onCancel={handleCancel} open={isModalOpen} >
                <Form  onFinish={retweetSubmit}>
                    <Input.TextArea 
                    value={content}
                    onChange={onChangeContent}
                    maxLength={200}
                    placeholder="인용 메모를 남겨보세요."
                    />
                  <OneLook> <img className='img1' src={`http://localhost:3065/${singlePost.Images[0]?.src}`} /> </OneLook>
                    <Button className='button1' htmlType="submit">인용하기</Button>
                </Form>
            </Modal>

        {liked ? 
        <OneLook>
        <Tooltip title="좋아요 취소">
        <HeartTwoTone onClick={onUnlike} twoToneColor="#fc83c6" className='icon2'/> 
        </Tooltip>
        </OneLook>
        :
        <OneLook> 
        <Tooltip title="좋아요">
        <HeartOutlined onClick={onLike} className='icon2' />
        </Tooltip>
        </OneLook>
        }

            <Col className='col1' >
            <input name="image" type="file" hidden ref={imageInput} onChange={onChangeImages} />
                <Card
                className='card1'
                
                cover={<OneLook><img className='img2' alt="example" src={ previewImg ? previewImg : `http://localhost:3065/${singlePost.Images[0]?.src}`} /></OneLook>}
                actions={
                    id && singlePost.UserId === me.id
                    ? 
                    [
                    <Tooltip title="이미지 수정">      
                    <Button onClick={onClickImageUpload} >      
                    <UploadOutlined key="upload"/>
                    </Button>
                    </Tooltip>   
                    ]
                    :
