<TextArea
  onChange={onChangeContent}
  showCount
  maxLength={250}
  value={content} 
  readOnly={read}
  className='textArea1'
/>

</Card>
    { id && singlePost.UserId === me.id
    ?
        (
            <>
            <OneLook>
            <Button 
            className='button4'>
                삭제하기
            </Button>
            <Button
            className='button5'
            htmlType='submit'
            >
                수정하기
            </Button>
            </OneLook>
            </>
        )
        :
        null
    }



</Col>

</div>
</Form>
</OneLook>

{/* 댓글 */}
<Col>
    {index === true  ? <NewComment post={singlePost} /> : null }
    {index === false  ? ( singlePost.Comments.length !== 0 ? <Comments post={singlePost} /> : <NoComments />) : null }
    <IfComments />
</Col>

    </AppLayout>
    );

