import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSquare } from '@fortawesome/free-regular-svg-icons';
import { Card, Empty, Modal } from 'antd';
import { useState } from 'react';
import { useSelector } from 'react-redux';

const Followers = () => {

  const {me} = useSelector((state) => state.user);

  const [isModalOpen, setIsModalOpen] = useState(false);

  console.log("팔로워리스트", me);

  const referModal = () => {
    setIsModalOpen(true);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const IfFollower = () => {

      if(me.Followers[0] !== undefined){
        return <li><FontAwesomeIcon icon={faSquare} style={{fontSize:15, color: 'pink'}} /> {me.Followers[0].nickname}</li>;
      }
      if(me.Followers[1] !== undefined){
        return <li><FontAwesomeIcon icon={faSquare} style={{fontSize:15, color: 'pink'}} />{me.Followers[1].nickname}</li>;
      }
      if(me.Followers[2] !== undefined){
        return <li><FontAwesomeIcon icon={faSquare} style={{fontSize:15, color: 'pink'}} />{me.Followers[2].nickname}</li>;
      }
      else{
        return  <div><Empty description={
          <span>
            아직 팔로워가 없어요.
          </span>
        } /></div>;
      }

  };

  const originalImage = `https://static.vecteezy.com/system/resources/previews/002/318/271/original/user-profile-icon-free-vector.jpg`;

    return(
      <div className="site-card-border-less-wrapper">
        <Card
          title="Followers"
          style={{width: 300, marginLeft: 50, height: 215}}
          extra={<a onClick={referModal}>More</a>}
        >
          <IfFollower />
        
        </Card>
        <Modal title={me.nickname + " 의 팔로워 목록"} footer={null} onCancel={handleCancel} open={isModalOpen}>
          {me.Followers.map((follower) => (
