import React, { createElement, useContext, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import moment from "moment";
import Link from 'next/link';
import { LOAD_USER_CALENDAR_DATA_REQUEST } from "../reducers/user";

const UserDateCellRender = (value) => {

   const dispatch = useDispatch();

   const context = useContext();

   const {userCalendarData} = useSelector((state) => state.user);

   useEffect(() => {
      dispatch({
         type: LOAD_USER_CALENDAR_DATA_REQUEST,
         data: context.params.id
      });
   }, []);

   // console.log("UserCalendarData:::::::::" + JSON.stringify(userCalendarData));
   // console.log("UserCalendarData길이: ", userCalendarData.length);

   const getDayInfo = () => {
      const calendarData = [];
      let diaryMoment;

      for( let i = 0; i < userCalendarData.length; i++ ){
         // console.log("생성 날짜",  moment(userCalendarData[i].createdAt).format("YYYY-MM-DD"));
         diaryMoment = moment(userCalendarData[i].createdAt).format("YYYY-MM-DD");

         // console.log("다이어리모맨트:", diaryMoment);

         if(diaryMoment){
            calendarData.push( {
               photo: userCalendarData[i].Images[0].src,
               title: userCalendarData[i].lookName,
               date: diaryMoment,
               createdAt: userCalendarData[i].createdAt,
               id: userCalendarData[i].id
            });
            // console.log("이미지???", userCalendarData[i].Images[0].src);
         }
      }

      // console.log("calendarData:::::" + JSON.stringify(calendarData));
      return calendarData || [];
   };

   const PhotoRander = ({date, photo, postId}) => {

   //   console.log("뉴데이트:::", new Date('2023-02-06T08:53:06.000Z').getDay());
   // console.log("캘린더 날짜: ", new Date(value).toLocaleDateString());
   // console.log("게시글 날짜: ", new Date(date).toLocaleDateString());
   
      switch(moment(value).format("YY-MM-DD")){
         case moment(date).format("YY-MM-DD"):  
         return (
            <a href={`http://localhost:3000/post/${postId}`} >
                <img style={{width: 50}} src={`http://localhost:3065/${photo}`} />
            </a>
         );
         default:
         return(
            <div></div>
         );   
      }
   };

   const NameRander = ({date, name}) => {

      switch(moment(value).format("YY-MM-DD")){
         case moment(date).format("YY-MM-DD") : 
         return(
            <h5>{name}</h5>
         );
         default:
         return(
            <div></div>
         );   
      }

   };

   const dayInfo = getDayInfo();

  //const photo = `http://localhost:3065/sean-kong-1dJmkdVuelg-unsplash_1669727155305.jpg`;

 return(
  <div>
   {dayInfo.map((info) => (

         <div key={info.id} >
            {/* <LookRander date={info.date} photo={info.photo} title={info.title} />  */}
              {/* <h5>{info.title}</h5>
            <Link href="#">
         <img style={{width: 50}} src={`http://localhost:3065/${info.photo}`} />
         </Link> */}
          <NameRander date={info.createdAt}  name={info.title} /> 
         <PhotoRander postId={info.id} date={info.createdAt} photo={info.photo} />
         </div>

      
   ))}
  </div>
 );
};



export default UserDateCellRender;
