import { Card, List } from "antd";
import axios from "axios";
import React from "react";
import { useSelector } from "react-redux";
import { END } from "redux-saga";
import { LOAD_POSTS_REQUEST } from "../../reducers/post";
import wrapper from "../../store/configureStore";
import Link from 'next/link';
import AppLayout from "../../components/AppLayout";
import { useRouter } from "next/router";
import styled from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import Meta from "antd/lib/card/Meta";

const OneSearch = styled.div`

    .h1{
        text-align: center;
    }
    .img1{
        height: 250px;
        width: 100%;
    }

`;

const HashtagResult = ({tag, post}) => {

    // const {mainPosts} = useSelector((state) => state.post);

            return(
                <OneSearch>
                <h1 className="h1"><FontAwesomeIcon icon={faMagnifyingGlass} /> Look with "#{tag}" </h1>
                {/* <List 
                grid={{
                    gutter: 50,
                    column: 4
                }}
                dataSource={post}
                renderItem={(items) => (
                    <List.Item>
                        <Card 
                        title={<a href={`http://localhost:3000/post/${items.id}`} >{items.lookName}</a>}
                        
                        cover={<a href={`http://localhost:3000/post/${items.id}`} ><img className="img1"alt="example" src={`http://localhost:3065/${items.Images[0]?.src}`} /></a>}
                        >
                            <Meta description={'#' + items.Hashtags[0].name} />
                        </Card>
                    </List.Item>
                )}
                /> */}
                <Card 
                        title={<a href={`http://localhost:3000/post/${post.id}`} >{post.lookName}</a>}
                        
                        cover={<a href={`http://localhost:3000/post/${post.id}`} ><img className="img1"alt="example" src={`http://localhost:3065/${post.Images[0]?.src}`} /></a>}
                        >
                            <Meta description={'#' + post.Hashtags[0].name} />
                        </Card>
                </OneSearch>
            );

};

export const getServerSideProps = wrapper.getServerSideProps(async(context) => { 
    
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = ''; 
    if(context.req && cookie){ 
        axios.defaults.headers.Cookie = cookie;
    }
    context.store.dispatch({
        type: LOAD_POSTS_REQUEST,
    });

   context.store.dispatch(END);
   await context.store.sagaTask.toPromise();
});

export default HashtagResult;
