import { Button, Calendar, Card, Row } from "antd";
import { useRouter } from "next/router";
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import AppLayout from "../../components/AppLayout";
import Followers from "../../components/followers";
import Followings from "../../components/followings";
import UserDateCellRender from "../../components/userComponent/userDateCellRender";
import UserInfo from "../../components/userComponent/userInfo";
import UserLikeList from "../../components/userComponent/userLikeList";
import UserMention from "../../components/userComponent/userMention";
import { LOAD_USER_REQUEST } from "../../reducers/user";
import wrapper from "../../store/configureStore";
import axios from "axios";
import { END } from "redux-saga";

const User = () => {

    const router = useRouter();
    const {id} = router.query;

    console.log("라우트: ", id);

    const {userInfo} = useSelector((state) => state.user);

    return(
        <AppLayout>
            <div style={{position: 'relative',width: 1250}}>
            <section style={{marginTop: '50px'}}>
            <Row><h1>{userInfo.nickname}'s Profile</h1></Row>
            <Row><Button href="/newLook" style={{backgroundColor: 'black', color: 'white'}}>오늘의 데일리룩 만들기</Button></Row>
            <Row>
            <div style={{float: 'left'}}>
            <Card>
                <Calendar 
                style={{width: 700, height: 893}}
                dateCellRender={UserDateCellRender}
                />

            </Card>
            </div>
            <div style={{float: 'left'}}>
                <UserInfo /> 
                <UserMention />
            </div>
            </Row>
            <Row style={{marginTop: 15}}>
                <UserLikeList />
                <Followers  />
                <Followings  />   
            </Row>  
            </section>
            </div>
        </AppLayout>
    );
};

export const getServerSideProps = wrapper.getServerSideProps(async(context) => { 
    
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = ''; 
    if(context.req && cookie){ 
        axios.defaults.headers.Cookie = cookie;
    }
    context.store.dispatch({
        type: LOAD_USER_REQUEST,
        data: context.params.id,
    });
    
   context.store.dispatch(END);
   await context.store.sagaTask.toPromise();
  });
  

export default User;
