import { Button, Rate, Row, Upload } from "antd";
import TextArea from "antd/lib/input/TextArea";
import {  useRouter } from "next/router";
import Router from "next/router";
import React, { useCallback, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
import { REMOVE_COMMENT_REQUEST, UPDATE_COMMENT_RATE_REQUEST } from "../reducers/post";

const OneSore = styled.div`
  .rate1{
    font-size: 50px;
  }
  .textArea1{
    height: 110px;
    resize: none;
  }
  .img1{
    width: 100px;
  }
  .button1{
    margin-top: 20px;
  }
`;

const Score = ({comments}) => {

  const dispatch = useDispatch();

  const router = useRouter();
  const {postId} = router.query;

  const id = useSelector((state) => state.user.me?.id);
  
  const {singlePost} = useSelector((state) => state.post);

  console.log("아이디: ", id);
  console.log("댓글: ", comments);

    const [fileList, setFileList] = useState([]);

    const onRemoveComment = useCallback(() => {
      if(!id){
          return alert('로그인이 필요합니다.');
      }

      const formData = new FormData();

      formData.append('PostId', singlePost.id);
      formData.append('commentId', comments[0].id);


      return dispatch({
          type: REMOVE_COMMENT_REQUEST,
          data: comments[0].id,
          type: UPDATE_COMMENT_RATE_REQUEST,
          data: formData
      },
      // Router.replace(`/post/${singlePost.id}`)
      );
  }, [id]);

          //미리보기
          const onPreview = async (file) => {
            let src = file.url;
            if (!src) {
              src = await new Promise((resolve) => {
                const reader = new FileReader();
                reader.readAsDataURL(file.originFileObj);
                reader.onload = () => resolve(reader.result);
              });
            }
            const image = new Image();
            image.src = src;
            const imgWindow = window.open(src);
            imgWindow?.document.write(image.outerHTML);
          };
    

    return(
        <>
        <OneSore>
        <Row><h2>My rate is...</h2></Row>
               <Row>
               {comments.map(comment => (
            <li key={comment.id}>
              <OneSore><Rate className="rate1" disabled defaultValue={comment.rate} /></OneSore> 
            </li>
             ))}
                </Row>  
              <br />
              <Row><h2>My commnet is...</h2></Row>
              
              <Row>
              {comments.map(comment => (
            <li key={comment.id}>
              <OneSore>
                <TextArea
                    showCount
                    maxLength={250}
                    defaultValue={comment.content}
                    readOnly
                    className="textArea1"
                />
                </OneSore>
            </li>
             ))}
                </Row>
                <br />
                <Row><h2>I recommend this!</h2></Row>
                {/* <Row><h3>아이템 추천은 2개까지 가능해요.</h3></Row> */}
                <Row>
                  
                <OneSore> { comments[0].Images[0] ? <img className="img1" src={`http://localhost:3065/${comments[0].Images[0].src}`} /> : null } </OneSore>
                <OneSore> { comments[0].Images[1] ? <img className="img1" src={`http://localhost:3065/${comments[0].Images[1].src}`} /> : null } </OneSore>

                </Row>
                {
                id && comments[0].UserId === id
                ?
                (
                  <>
                  <Button type="danger" className="button1" onClick={onRemoveComment} >댓글 삭제</Button>
                  </>
                )
                :
                null
                }
                
        </OneSore>
        </>
    );
}


export default Score;
