import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { Card, Empty, List, Modal } from 'antd';
import { useState } from 'react';
import { useSelector } from 'react-redux';
import styled from 'styled-components';

const One = styled.div`
  .icons{
    font-size:15px;
  }
  .card1{
    width: 250px; 
    height: 180px;
    margin-top: 10px;
    margin-left: 19px;
  }
  .img1{
    width: 100%;
  }
`;

const LikeList = () => {

  const {me} = useSelector((state) => state.user);

  const [isModalOpen, setIsModalOpen] = useState(false);

  console.log("인용리스트", me);

  const referModal = () => {
    setIsModalOpen(true);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const IfLike = () => {

      if(me.Liked[0] !== undefined){
        return <li><FontAwesomeIcon icon={faHeart} className='icons' /> {me.Liked[0].lookName}</li>;
      }
      if(me.Liked[1] !== undefined){
        return <li><FontAwesomeIcon icon={faHeart} className='icons' />{me.Liked[1].lookName}</li>;
      }
      if(me.Liked[2] !== undefined){
        return <li><FontAwesomeIcon icon={faHeart} className='icons' />{me.Liked[2].lookName}</li>;
      }
      else{
        return  <div>
                  <Empty
                  description={
                    <span>
                      아직 좋아요를 한 Look이 없어요.
                    </span>
                  } 
                  />
                </div>;
      }
  
  };


    return(
        <div className="site-card-border-less-wrapper">
          <One>
            <Card
              title="I like this!"
              className='card1'
              extra={<a onClick={referModal}>More</a>}
            >
              <IfLike />
            </Card>
            <Modal title={me.nickname + " 의 좋아요 목록"} footer={null} onCancel={handleCancel} open={isModalOpen}>
            <List
               grid={{
                gutter: 50,
                column: 3
            }}
              dataSource={me.Liked}
              renderItem={(item) => (
                <List.Item>
                    <Card 
                    title={<a href={`http://localhost:3000/post/${item.id}`} >{item.lookName}</a>}
                    cover={<a href={`http://localhost:3000/post/${item.id}`} ><One><img className='img1' alt="example" src={`http://localhost:3065/${item.Images[0].src}`} /></One></a>}
                    className='card2'
                    >
                    </Card>
                </List.Item>
            )}
            >
                
            </List>

            </Modal>
        </One>
      </div>
    );

};

export default LikeList;
