    const dispatch = useDispatch();

    const {me} = useSelector((state) => state.user);


    const imageInput = useRef();

    // const top = useRef();
    // const bottom = useRef();
    // const dress = useRef();
    // const shoes = useRef();
    // const acc = useRef();
    // const outer = useRef();
    // const lookName = useRef();

