import React, { useCallback, useEffect, useRef, useState } from "react";
import '../../pages/css/myInfo.module.css';
import { Button, Card, Form, Input, Tooltip } from 'antd';
import Router from 'next/router';
import { EditOutlined } from '@ant-design/icons';
// import useInput from "../hooks/useInput";
import { useDispatch, useSelector } from "react-redux";
import { LOAD_USER_REQUEST } from "../../reducers/user";
import wrapper from "../../store/configureStore";
import axios from "axios";
import { END } from "redux-saga";
const { Meta } = Card;

const UserInfo = () => {

  const dispatch = useDispatch();

  const {userInfo} = useSelector((state) => state.user);

  const [previewImg, setPreviewImg] = useState(null);

  const [read, setRead] = useState(true);

  // const [text, onChangeText, setText] = useInput('');

  const imageInput = useRef(); 

    return(
        <div>
          <Form encType="multipart/form-data" onFinish={onSubmit}>
        <Card
            title="My Info"
            style={{marginLeft: 15, width: 250}}
            cover={
                
            <img src={`http://localhost:3065/${userInfo.Image.src}`}  />
            
            }

        >
    <input name="profile" type="file" hidden ref={imageInput} onChange={onChangeImages} />
    <Meta
      title={<Input  readOnly defaultValue={userInfo.nickname} />}
      description={
        <Input  readOnly defaultValue={userInfo.info} />
      }
    />
  </Card>
  </Form>
        </div>
    );

};

export const getServerSideProps = wrapper.getServerSideProps(async(context) => { 
    
  const cookie = context.req ? context.req.headers.cookie : '';
  axios.defaults.headers.Cookie = ''; 
  if(context.req && cookie){ 
      axios.defaults.headers.Cookie = cookie;
  }
  context.store.dispatch({
      type: LOAD_USER_REQUEST,
      data: context.params.id
  });
  
 context.store.dispatch(END);
 await context.store.sagaTask.toPromise();
});


export default UserInfo;
