import { Button, Rate, Row, Upload } from "antd";
import TextArea from "antd/lib/input/TextArea";
import React, { useState } from "react";
import { useSelector } from "react-redux";

const Score = ({comments}) => {

  const id = useSelector((state) => state.user.me?.id);

    console.log("개별댓글 점수", comments[0].rate)
  console.log("댓글 안?? ", comments[0].Images[0].src);
  console.log("아이디: ", id);
  console.log("댓글: ", comments);

    const [fileList, setFileList] = useState([]);

    

          //미리보기
          const onPreview = async (file) => {
            let src = file.url;
            if (!src) {
              src = await new Promise((resolve) => {
                const reader = new FileReader();
                reader.readAsDataURL(file.originFileObj);
                reader.onload = () => resolve(reader.result);
              });
            }
            const image = new Image();
            image.src = src;
            const imgWindow = window.open(src);
            imgWindow?.document.write(image.outerHTML);
          };
    

    return(
        <>
        <Row><h2>My rate is...</h2></Row>
               <Row>
               {comments.map(comment => (
            <li key={comment.id}>
                <Rate style={{fontSize: 50}} disabled defaultValue={comment.rate} />
            </li>
             ))}
                {/* <Rate style={{fontSize: 50}} onChange={handleRate} value={post.Comments.rate} /> */}
                </Row>  
              <br />
              <Row><h2>My commnet is...</h2></Row>
              
              <Row>
              {comments.map(comment => (
            <li key={comment.id}>
                <TextArea
                    showCount
                    maxLength={250}
                    defaultValue={comment.content}
                    readOnly
                    style={{
                        height: 110,
                        resize: 'none',
                    }}
                />
            </li>
             ))}
                </Row>
                <br />
                <Row><h2>I recommend this!</h2></Row>
                {/* <Row><h3>아이템 추천은 2개까지 가능해요.</h3></Row> */}
                <Row>
                  
              { comments[0].Images[0] ? <img style={{width: 100}} src={`http://localhost:3065/${comments[0].Images[0].src}`} /> : null }
              { comments[0].Images[1] ? <img style={{width: 100}} src={`http://localhost:3065/${comments[0].Images[1].src}`} /> : null }

                </Row>
                {/* {id && comments} */}
                <Button >댓글 삭제</Button>
        </>
    );
}


export default Score;
