import { Card, Empty, Modal } from 'antd';
import { EditOutlined } from '@ant-design/icons';
import { useSelector } from 'react-redux';
import { useState } from 'react';
import { useRouter } from 'next/router';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPenNib } from '@fortawesome/free-solid-svg-icons';
import wrapper from '../../store/configureStore';
import axios from 'axios';
import { END } from 'redux-saga';
const { Meta } = Card;


const UserMention = () => {

  const {userInfo} = useSelector((state) => state.user);

  const router = useRouter();
  const {id} = router.query;

  const [isModalOpen, setIsModalOpen] = useState(false);

  const referModal = () => {
    setIsModalOpen(true);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const IfRefer = () => {

    // for(let i = 0; i < me.References.length; i++){
    //   return( <li>{me.References[i].content}</li>);
    // }

      if(userInfo.References[0] !== undefined){
        return <li><FontAwesomeIcon icon={faPenNib} style={{fontSize: 15}} />{userInfo.References[0].content}</li>;
      }
      if(userInfo.References[1] !== undefined){
        return <li><FontAwesomeIcon icon={faPenNib} style={{fontSize: 15}} />{userInfo.References[1].content}</li>;
      }
      if(userInfo.References[2] !== undefined){
        return <li><FontAwesomeIcon icon={faPenNib} style={{fontSize: 15}} />{userInfo.References[2].content}</li>;
      }
      else{
        return  <div>
                  <Empty
                  description={
                    <span>
                      아직 팔로잉이 없어요.
                    </span>
                  } 
                  />
                </div>;
                }

  };

    return(
        <div>
            <Card
            title="I refered..."
            style={{
                width: 250, height: 215, float: 'left', marginLeft: 17, marginTop: 30}}
            extra={<a onClick={referModal}>More</a>}
            >
              <IfRefer />
            </Card>
            <Modal title={userInfo.nickname + " 의 인용 목록"} footer={null} onCancel={handleCancel} open={isModalOpen} >
              {userInfo.References.map((refer) => (
                <ul key={refer.id}>
                  <p><FontAwesomeIcon icon={faPenNib} style={{fontSize: 20}} /> <img style={{width: 60}} src={`http://localhost:3065/${refer.referSrc}`}/> <br /> {refer.content}</p>
                </ul>
              ))}
            </Modal>
        </div>
    );
};

