import React, { useEffect } from "react";
import '../../pages/css/myInfo.module.css';
import { Button, Card, Form, Input, Tooltip } from 'antd';
import Router, { useRouter } from 'next/router';
import { EditOutlined } from '@ant-design/icons';
import { useDispatch, useSelector } from "react-redux";
import { LOAD_USER_REQUEST } from "../../reducers/user";
import wrapper from "../../store/configureStore";
import axios from "axios";
import { END } from "redux-saga";
const { Meta } = Card;
import styled from "styled-components";

const OneUserInfo = styled.div`
  .card1{
    margin-left: 15px;
    width: 250px;
  }
  .img1{
    height: 100%;
  }
`;

const originalImage = `https://static.vecteezy.com/system/resources/previews/002/318/271/original/user-profile-icon-free-vector.jpg`;

const UserInfo = () => {

  const {userInfo} = useSelector((state) => state.user);

  console.log("유저인포: ", userInfo);

    return(
      <div>
        <OneUserInfo>
            <Form encType="multipart/form-data" >
                <Card
                    title={userInfo.nickname+"'s Info"}
                    className='card1'
                    cover={<OneUserInfo><img className="img1" src={ userInfo.Image.src === originalImage ? userInfo.Image.src : `${userInfo.Image.src}`} /></OneUserInfo>}
                >
                <Meta
                  title={<Input  readOnly defaultValue={userInfo.nickname} />}
                  description={
                    <Input  readOnly defaultValue={userInfo?.info} />
                  }
                />
              </Card>
          </Form>
        </OneUserInfo>
      </div>
    );

};

export default UserInfo;
