import React, { useCallback } from "react";
import {Input, Menu, Row, Col} from 'antd';
import Link from 'next/link';
import useInput from "../hooks/useInput";
import { Router } from "next/router";


const AppLayout = ({children}) => {

    const [searchInput, onChangeSearchInput] = useInput('');

    const onSearch = useCallback(() => {
        Router.push(`/hashtag/${searchInput}`);
      }, [searchInput]);

    return(
        <div >
               <Menu mode='horizontal' style={{width: 1800, backgroundColor: 'black', color:'white'}}>    
               
                <Menu.Item >
                <Link href="/"><h1 style={{color:'white', marginTop: 5}}>FASHIONARY</h1></Link>
                </Menu.Item>
                <Menu.Item>
                <Link href="/profile"><h3 style={{color:'white', marginTop: 5}}>My Profile</h3></Link>
                </Menu.Item >
                <Menu.Item>
                <Link href="/post/allLooks"><h3 style={{color:'white', marginTop: 5}}>All Looks</h3></Link>
                </Menu.Item>
                <Menu.Item style={{background: 'black', height: 50}}>
                    <Input.Search 
                    style={{marginTop: 12}}
                    value={searchInput}
                    onChange={onChangeSearchInput}
                    onSearch={onSearch}
                    />
                </Menu.Item>
            </Menu>
            <Row gutter={8}>
                <Col xs={24} md={6}>
                    {/* {me ? <UserProfile /> : <Loginform />} */}
                </Col>
                <Col xs={24} md={12}>{children}</Col>
            </Row>
            
        </div>
    );

};

export default AppLayout;
