import AppLayout from "../components/AppLayout";
import { Button, Card, Col, Form, Input, Select, Space, Upload  } from 'antd';
import { EditOutlined, UploadOutlined  } from '@ant-design/icons';
import TextArea from "antd/lib/input/TextArea";
import { useDispatch, useSelector } from "react-redux";
import { useCallback, useEffect, useRef, useState } from "react";
import useInput from "../hooks/useInput";
import { ADD_POST_REQUEST, REMOVE_IMAGE, UPLOAD_IMAGES_REQUEST } from "../reducers/post";
const { Meta } = Card;
const { Option } = Select;

const NewLook = () => {

    const dispatch = useDispatch();

    const imageInput = useRef();

    const {imagePaths, addPostDone} = useSelector((state) => state.post);

    const [previewImg, setPreviewImg] = useState(null);

    const [text, onChangeText, setText] = useInput('');

    useEffect(() => {
        if(addPostDone){
            setText('');
        }
    }, [addPostDone]);

    const onSubmit = useCallback(() => {
        if(!text || !text.trim()){
            return alert('게시글을 작성하세요.');
        }
        const formData = new FormData();
        imagePaths.forEach((p) => {
            formData.append('image', p);
        });
        formData.append('content', text);
        dispatch({
            type: ADD_POST_REQUEST,
            data: formData,
        //     data:{  이렇게 보내도 됨.
        //         imagePaths,
        //         content: text
        //     }
         });
        console.log("front text: " + text);
    },[text, imagePaths]);

    const onClickImageUpload = useCallback(() => {
        imageInput.current.click();
    }, [imageInput.current]);

    const onChangeImages = useCallback((e) => {
        console.log('images', e.target.files);
        const imageFormData = new FormData();
        [].forEach.call(e.target.files, (f) => {
            imageFormData.append('image', f);
        });
       return dispatch({
            type: UPLOAD_IMAGES_REQUEST,
            data: imageFormData
        });
    });
    

    const onRemoveImage = useCallback((index) => () => {
        dispatch({
            type: REMOVE_IMAGE,
            data: index
        });
    });

    return(
        <AppLayout >
            <div style={{width: 1120}}>
            <h1>New DailyLook</h1>
            <Form encType="multipart/form-data" onFinish={onSubmit}>
                <Col style={{float: 'left'}}>
                    <Card
                    style={{
                    width: 280,
                    }}
                    
                    cover={
                        <img
                          alt="example"
                          src={previewImg ? previewImg : 'http://www.cams-it.com/wp-content/uploads/2015/05/default-placeholder-250x200.png'}
                         ref={imageInput}
                          height='580px'
                        />
                            }

                    actions={[
                        <Upload>
                            <UploadOutlined />
                        </Upload>
                    ]}
                    >  
                      {imagePaths.map((v,i) => (
                    <div key={v} style={{display: 'inline-block'}}>
                        <img src={`http://localhost:3065/${v}`} style={{width: '200px'}} alt={v}/>
                    <div>
                    <EditOutlined onClick={onRemoveImage(i)} key="edit" />
                    </div>
                    </div>
                ))}  
                    </Card>
                </Col>
            <Col style={{float: 'left', width:500}}>
            <div style={{ }}>
                <ul>
                <li style={{display: 'inline-block'}}><h3 >데일리룩 제목</h3></li>
                <li style={{display: 'inline-block', marginLeft:10}}><Input showCount maxLength={20} /></li>
                </ul>
                
                <ul>
                <li style={{display: 'inline-block'}}><h3 >카테고리</h3></li>
                <li style={{display: 'inline-block', marginLeft:18}}>
                <Select
                    defaultValue="카테고리를 선택하세요."
                    style={{
                    width: 200,
                    marginLeft: 30
                    }}
                >
                    <Option value="jack">Jack</Option>
                    <Option value="lucy">Lucy</Option>
                </Select>
                </li>
                </ul>
                </div>
                <div>
                <Card style={{marginLeft: 15, marginTop: 0,width:400}}>
                <Button style={{backgroundColor: 'black', color: 'white', marginBottom: 10}} block >Information</Button>
    <thead>
        <tr >
        <th>분류</th>
        <th>브랜드명</th>
        
        </tr>
    </thead>
    <tbody style={{textAlign:'center'}} >
        <tr>
        <td> <Input disabled  defaultValue="상의" style={{color: 'black', textAlign:'center'}} /></td>
        <td> <Input   /></td> 
        </tr>

        <tr>
        <td> <Input disabled  defaultValue="하의" style={{color: 'black', textAlign:'center'}} /></td>
        <td> <Input   /></td> 
        </tr>

        <tr>
        <td> <Input disabled  defaultValue="원피스" style={{color: 'black', textAlign:'center'}} /></td>
        <td> <Input   /></td> 
        </tr>

        <tr>
        <td> <Input disabled  defaultValue="아우터" style={{color: 'black', textAlign:'center'}} /></td>
        <td> <Input   /></td> 
        </tr>

        <tr>
        <td> <Input disabled  defaultValue="신발" style={{color: 'black', textAlign:'center'}} /></td>
        <td> <Input   /></td> 
        </tr>

        <tr>
