import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSquare } from '@fortawesome/free-regular-svg-icons';
import { Card, Empty, List, Modal } from 'antd';
import { useState } from 'react';
import { useSelector } from 'react-redux';

const UserFollowers = () => {

    const {userInfo} = useSelector((state) => state.user);

  const [isModalOpen, setIsModalOpen] = useState(false);

  console.log("유저프로필: ", userInfo);

  const referModal = () => {
    setIsModalOpen(true);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const IfUserFollower = () => {

      if(userInfo.Followers[0] !== undefined){
        return <li><FontAwesomeIcon icon={faSquare} style={{fontSize:15, color: 'pink'}} /> {userInfo.Followers[0].nickname}</li>;
      }
      if(userInfo.Followers[1] !== undefined){
        return <li><FontAwesomeIcon icon={faSquare} style={{fontSize:15, color: 'pink'}} />{userInfo.Followers[1].nickname}</li>;
      }
      if(userInfo.Followers[2] !== undefined){
        return <li><FontAwesomeIcon icon={faSquare} style={{fontSize:15, color: 'pink'}} />{userInfo.Followers[2].nickname}</li>;
      }
      else{
        return  <div>
                    <Empty 
                    description={
                      <span>
                        아직 팔로워가 없어요.
                      </span>
                    } 
                    />
                </div>;
      }

  };

  const originalImage = `https://static.vecteezy.com/system/resources/previews/002/318/271/original/user-profile-icon-free-vector.jpg`;

    return(
      <div className="site-card-border-less-wrapper">
        <Card
          title="Followers"
          style={{width: 300, marginLeft: 50, height: 215}}
          extra={<a onClick={referModal}>More</a>}
        >
          <IfUserFollower />
        
        </Card>
        <Modal title={userInfo.nickname + " 의 팔로워 목록"} footer={null} onCancel={handleCancel} open={isModalOpen}>
         

            <List
               grid={{
                gutter: 50,
                column: 3
              }}
              dataSource={userInfo.Followers}
              renderItem={(item) => (
                <List.Item>
                    <Card
                    title={<a href={`http://localhost:3000/post/${item.id}`} >{item.nickname}</a>}
                    cover={<a href={`http://localhost:3000/post/${item.id}`} ><img style={{width: '100%'}} alt="example" src={item.Image.src === originalImage ? originalImage : `http://localhost:3065/${item.Image.src}`} /></a>}
                    style={{width:110}}
                    >
                    </Card>
                </List.Item>
            )}
            >
          </List> 
        </Modal>
      </div>
    );

};

// {me.Followers.map((follower) => (
//   <ul key={follower.id}>
//     <p><FontAwesomeIcon icon={faSquare} style={{fontSize:15, color: 'pink'}} /> 
//     <img style={{width: 60}} src={following.Image.src === originalImage ? originalImage : `http://localhost:3065/${follower.Image.src}`} />
//     </p>
//   </ul>
//   ))}

export default UserFollowers;
