import React, { useCallback } from "react";
import {Input, Menu, Row, Col} from 'antd';
import Link from 'next/link';
import useInput from "../hooks/useInput";


const AppLayout = ({children}) => {

    const [searchInput, onChangeSearchInput] = useInput('');

    const onSearch = useCallback(() => {
        Router.push(`/hashtag/${searchInput}`);
      }, [searchInput]);

    return(
        <div >
               <Menu mode='horizontal' style={{width: 1800, backgroundColor: 'black', color:'white'}}>    
               
                <Menu.Item >
                <Link href="/"><h1 style={{color:'white'}}>FASHIONARY</h1></Link>
                </Menu.Item>
                <Menu.Item>
                <Link href="/profile"><a style={{color:'white', marginTop: 20}}>내 프로필</a></Link>
                </Menu.Item >
                <Menu.Item style={{background: 'black'}}>
                    <Input.Search 
                    style={{marginTop: 12}}
                     onSearch={onSearch}
                    />
                </Menu.Item>
            </Menu>
            <Row gutter={8}>
                <Col xs={24} md={6}>
                    {/* {me ? <UserProfile /> : <Loginform />} */}
                </Col>
                <Col xs={24} md={12}>{children}</Col>
            </Row>
            
        </div>
    );

};

export default AppLayout;
