import { Button, Card, Col, Form, Input, Modal, Row, Select, Space, Switch, Tooltip, Upload  } from 'antd';
import { EditOutlined, HeartOutlined, HeartTwoTone, UploadOutlined  } from '@ant-design/icons';
import TextArea from "antd/lib/input/TextArea";
import AppLayout from '../../components/AppLayout';
import { FOLLOW_REQUEST, LOAD_MY_INFO_REQUEST, UNFOLLOW_REQUEST } from '../../reducers/user';
import { LIKE_POST_REQUEST, LOAD_POST_REQUEST, RETWEET_REQUEST, UNLIKE_POST_REQUEST, UPDATE_POST_REQUEST, UPLOAD_UPDATE_LOOK_IMAGE_REQUEST } from '../../reducers/post';
import wrapper from '../../store/configureStore';
import { END } from 'redux-saga';
import { useRouter } from 'next/router';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import Look from '../look';
import NewComment from '../../components/newComment';
import { useCallback, useEffect, useRef, useState } from 'react';
import Comments from '../../components/comments';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faPenToSquare} from "@fortawesome/free-regular-svg-icons";
import {faHeartPulse} from "@fortawesome/free-solid-svg-icons";
import { faHeartCrack } from '@fortawesome/free-solid-svg-icons';
import { faHouse } from '@fortawesome/free-solid-svg-icons';
import NoComments from '../../components/noComments';
import '../css/post[id].module.css';
import useInput from '../../hooks/useInput';
const { Meta } = Card;
const { Option } = Select;
import styled from 'styled-components';
import patchInput from '../../hooks/patchInput';

const OneLook = styled.div`

    .div1{
        width: 1120px;
        position: relative;
        z-index: 1;
    }
    .switch1{
        margin-left: 900px;
        margin-top: 36px;
    }
    .icon1{
        position: absolute;
        z-index: 5;
        font-size: 30px;
        margin-left: 200px;
        margin-top: 10px;
    }
    .icon2{
        position: absolute;
        z-index: 5;
        font-size: 30px;
        margin-left: 243px;
        margin-top: 10px;
    }
    .icon3{
        z-index:10;
    }
    .img1{
        margin-left: 120px;
        margin-top: 20px;
        height: 330px;
    }
    .img2{
        height: 550px;
        width: 100%;
    }
    .button1{
        margin-left: 384px;
    }
    .button2{
        background-color: black;
        color: white;
        margin-bottom: 10px;
    }
    .button3{
        background-color: black;
        color: white;
        margin-bottom: 10px;
    }
    .button4{
        margin-left: 20%;
        margin-top: 5px; 
        background-color: black;
        color: white;
    }
    .button5{
       margin-left: 10%;
        margin-top: 5px;
        background-color: black;
        color: white;
    }
    .col1{
        float: left;
    }
    .card1{
        width: 280px;
        z-index: 1;
    }
    .card2{
        margin-left: 15px;
        width: 400px;
    }
    .card3{
        margin-top: 24px; 
        margin-left: 15px;
        width: 400px;
        height: 245px;
    }
    .li1{
        display: inline-block;
    }
    .li2{
        display: inline-block;
        margin-left: 10px;
    }
    .body1{
        text-align: center;
    }
    .input1{
        color: black;
        text-align: center;
    }
    .textArea1{
        height: 110px;
        resize: none;
        margin-top: 20px;
    }
`;

const Post = () => {

 
    const dispatch = useDispatch();
    const router = useRouter();
    const {id} = router.query;
    const {userCalendarData, me, followLoading, unfollowLoading} = useSelector((state) => state.user);
    const {imagePaths, singlePost, retweetError, retweetDone} = useSelector((state) => state.post);
    const [read, setRead] = useState(true);
    const [previewImg, setPreviewImg] = useState(null);

    // const [top, setTop] = useState('');

    // const [top, onChangeTop ,setTop] = useInput('');
    const [bottom, onChangeBottom ,setBottom] = useInput('');
    const [dress, onChangeDress ,setDress] = useInput('');
    const [shoes, onChangeShoes ,setShoes] = useInput('');
    const [acc, onChangeAcc ,setAcc] = useInput('');
    const [outer, onChangeOuter ,setOuter] = useInput('');
    const [lookName, onChangeLookName ,setLookName] = useInput('');
    const [ content, onChangeContent, setContent ] = useInput('');

    const [top, setTop] = useState(singlePost.top);
    const onChangeTop = useCallback((e) => {
        const value = e.target.value;
        setTop((top) => top = value);

    }, []);



    // const onChangeTop = useCallback((e) => {
    //     if(e.target.value){
    //         setTop(e.target.value);
    //     }
    //     if(e.target.value === null){
    //         setTop(singlePost.top);
    //     }
    //     console.log("탑: ",top);
    // }, []);

    const imageInput = useRef();

    useEffect(() => {
        dispatch({
            type: LOAD_POST_REQUEST,
            data: id
        });
        if(me.id === singlePost.UserId){
            setRead(false);
        }
    }, [top]);


    const [index, setIndex] = useState(true);

    const [isModalOpen, setIsModalOpen] = useState(false);

    console.log("singlePost::::: ", singlePost);

    const liked = singlePost.Likers[0];

    const isFollowing = me?.Followings.find((v) => v.id === singlePost.UserId);

    console.log("좋아요: ", liked);

    console.log("텍스트: ", content);

    const tags = [];

    for(let i = 0; i < singlePost.Hashtags.length; i++){
        
        if(singlePost.Hashtags !== undefined){
            tags.push( "#" + singlePost.Hashtags[i].name );
        }
    }

    console.log("해시태그: ", tags);

    //댓글 쓰기, 보기 선택
    const onSelect = () => {
        setIndex(!index);
        console.log("index: ", index);
      };

      //리트윗 에러
      useEffect(() => {
        if(retweetError){
            window.alert(retweetError);
        }
      },[retweetError]);

      //좋아요
      const onLike = useCallback(() => {
        if(!id){
            return alert('로그인이 필요합니다.');
        }
       return dispatch({
                type: LIKE_POST_REQUEST,
                data: id
            });
        },[id]);

        //좋아요 취소
        const onUnlike = useCallback(() => {
            if(!id){
                return alert('로그인이 필요합니다.');
            }
            return dispatch({
            type: UNLIKE_POST_REQUEST,
            data: id
           });
        },[id]);

        //팔로우
        const onClickButton = useCallback(() => {
            if(isFollowing){
                dispatch({
                    type: UNFOLLOW_REQUEST,
                    data: singlePost.UserId
                }, window.alert("언팔로우 합니다."));
            }
            else{
                dispatch({
                    type: FOLLOW_REQUEST,
                    data: singlePost.UserId
                }, window.alert("팔로우 합니다."));
            }
        },[isFollowing]);

        //리트윗모달
        const retweetModal = () => {
            if(!id){
                return alert('로그인이 필요합니다.');
            }
            setIsModalOpen(true);
        };

        //리트윗
        const retweetSubmit = useCallback(() => {
            const formData = new FormData();

            formData.append('content', content);
            formData.append('postId', singlePost.id);

            dispatch({
                type: RETWEET_REQUEST,
                data: formData
            },
            router.reload());
        }, [content]);
        
        //리트윗 모달 창 닫기
        const handleCancel = () => {
            setIsModalOpen(false);
          };

    //댓글 없을 때
    const IfComments = () => {
        if(index === false){
            if(singlePost.Comments.length === 0){
                return (
                    <NoComments />
                );
            }else{
                <Comments post={singlePost} />
            }
        }else{
            return(
                null
            );
        }
    };

    const onClickImageUpload = useCallback(() => {
        imageInput.current.click();
    }, [imageInput.current]);


//게시글 수정 요청
    const onSubmit = useCallback(() => {

        const formData = new FormData();
        imagePaths.forEach((p) => {
            formData.append('image', p);
        });
        formData.append('content', content);
        formData.append('top', top);
        formData.append('bottom', bottom);
        formData.append('dress', dress);
        formData.append('shoes', shoes);
        formData.append('acc', acc);
        formData.append('outer', outer);
        formData.append('lookName', lookName);
        formData.append('postId', id);
        dispatch({
            type: UPDATE_POST_REQUEST,
            data: formData,
         });
    },[content, imagePaths]);

    //이미지 미리보기
    const onChangeImages = useCallback((e) => {
    
        const reader = new FileReader();
        if(e.target.files[0]){
          reader.readAsDataURL(e.target.files[0]);
        }
        reader.onloadend = () => {
          const previewImgUrl = reader.result;
          if(previewImgUrl){
            setPreviewImg(previewImgUrl);
          }
        }
        const imageFormData = new FormData();
   
        [].forEach.call(e.target.files, (f) => {
          imageFormData.append('profile', f);
      });
    
       return dispatch({
            type: UPLOAD_UPDATE_LOOK_IMAGE_REQUEST,
            data: imageFormData
        });

    });


    return(
        <AppLayout >
            <OneLook>
            <Form encType="multipart/form-data" onFinish={onSubmit}>
        <div className='div1'>
       <Row>
        <h1>DailyLook</h1>
        <Switch defaultChecked={index} onChange={onSelect} className='switch1' checkedChildren="댓글쓰기" unCheckedChildren="댓글보기" />
       </Row>
       {me.id === singlePost.UserId 
            ?
            null
            :
            <OneLook>
            <Tooltip title="내 계정에 인용하기">
            <FontAwesomeIcon 
                    className='icon1'
                    icon={faPenToSquare} 
                    onClick={retweetModal}
            
            />
            </Tooltip>
            </OneLook>
       }
      
            <Modal title="인용하기" footer={null} onCancel={handleCancel} open={isModalOpen} >
                <Form  onFinish={retweetSubmit}>
                    <Input.TextArea 
                    value={content}
                    onChange={onChangeContent}
                    maxLength={200}
                    placeholder="인용 메모를 남겨보세요."
                    />
                    <img className='img1' src={`http://localhost:3065/${singlePost.Images[0].src}`} /> 
                    <Button className='button1' htmlType="submit">인용하기</Button>
                </Form>
            </Modal>

        {liked ? 
        <OneLook>
        <Tooltip title="좋아요 취소">
        <HeartTwoTone onClick={onUnlike} twoToneColor="#fc83c6" className='icon2'/> 
        </Tooltip>
        </OneLook>
        :
        <OneLook> 
        <Tooltip title="좋아요">
        <HeartOutlined onClick={onLike} className='icon2' />
        </Tooltip>
        </OneLook>
        }

            <Col className='col1' >
            <input name="image" type="file" hidden ref={imageInput} onChange={onChangeImages} />
                <Card
                className='card1'
                
                cover={<OneLook><img className='img2' alt="example" src={ previewImg ? previewImg : `http://localhost:3065/${singlePost.Images[0].src}`} /></OneLook>}
                actions={
                    id && singlePost.UserId === me.id
                    ? 
                    [
                    <Tooltip title="이미지 수정">      
                    <Button onClick={onClickImageUpload} >      
                    <UploadOutlined key="upload"/>
                    </Button>
                    </Tooltip>   
                    ]
                    :
                   [ 
                   <OneLook>
                    {isFollowing
                    ?
                    <Tooltip title='언팔로우'>
                        <FontAwesomeIcon
                        icon={faHeartCrack}
                        fontSize={20}
                        onClick={onClickButton}
                        className='icon3'
                        />
                    </Tooltip>
                    :
                    <Tooltip title={isFollowing ? '언팔로우' : '팔로우'}>
                        <FontAwesomeIcon
                        icon={faHeartPulse}
                        fontSize={20}
                        onClick={onClickButton}
                        className='icon3'
                        key='unfollow'
                        />
                    </Tooltip>
                    }
                    </OneLook>
                    ,
                    <OneLook>
                    <Tooltip title={singlePost.User.nickname + "의 홈 방문하기"}>
                        <a href={`http://localhost:3000/user/${singlePost.UserId}`}>
                        <FontAwesomeIcon
                         icon={faHouse} 
                         fontSize={20}
                         className='icon3'
                         key='home'
                         />
                        </a>
                    </Tooltip>
                    </OneLook>
                    ]
                }
                >      
                <Meta title={"글쓴이:  " + singlePost.User.nickname} />
                </Card>
                <h1>This look is...{singlePost.rateAverage}points.</h1>
            </Col>
        <Col style={{float: 'left', width:500}}>
        <div>
            <ul>
            <li className='li1'><h3 >데일리룩 제목</h3></li>
            <li className='li2'><Input name='lookname' readOnly={read} defaultValue={singlePost.lookName} onChange={onChangeLookName}  /></li>
            </ul>

            </div>
            <div>
            <Card className='card2'>
            <Button className='button2' block >Information</Button>
<thead>
    <tr >
    <th>분류</th>
    <th>브랜드명</th>
    
    </tr>
</thead>
<tbody className='body1' >
    <tr>
    <td> <Input disabled defaultValue="상의" className='input1' /></td>
    <td> <Input name='top' value={top} readOnly={read} onChange={onChangeTop}  type="text"  /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="하의" className='input1' /></td>
    <td> <Input  defaultValue={singlePost.bottom} readOnly={read} onChange={onChangeBottom} type="text" /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="원피스" className='input1' /></td>
    <td> <Input  defaultValue={singlePost.dress} readOnly={read} onChange={onChangeDress} type="text" /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="아우터" className='input1' /></td>
    <td> <Input  defaultValue={singlePost.outer} readOnly={read} onChange={onChangeOuter} type="text" /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="신발" className='input1' /></td>
    <td> <Input defaultValue={singlePost.shoes} readOnly={read} onChange={onChangeShoes}  type="text" /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="패션잡화" className='input1' /></td>
    <td> <Input defaultValue={singlePost.acc} readOnly={read}  onChange={onChangeAcc}  type="text" /></td> 
    </tr>

</tbody>
</Card>
</div>
<Card className='card3'>
<Button className='button3' block >Daily Record </Button>
<h4>해시태그: {tags}</h4>
<TextArea
  onChange={onChangeContent}
  showCount
  maxLength={250}
  defaultValue={singlePost.content} 
  readOnly={read}
  className='textArea1'
/>

</Card>
    { id && singlePost.UserId === me.id
    ?
        (
            <>
            <OneLook>
            <Button 
            className='button4'>
                삭제하기
            </Button>
            <Button
            className='button5'
            htmlType='submit'
            >
                수정하기
            </Button>
            </OneLook>
            </>
        )
        :
        null
    }



</Col>

</div>
</Form>
</OneLook>

{/* 댓글 */}
<Col>
    {index === true  ? <NewComment post={singlePost} /> : null }
    {index === false  ? ( singlePost.Comments.length !== 0 ? <Comments post={singlePost} /> : <NoComments />) : null }
    {/* <IfComments /> */}
</Col>

    </AppLayout>
    );

};

export const getServerSideProps = wrapper.getServerSideProps(async(context) => { 
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = ''; 
    if(context.req && cookie){ 
        axios.defaults.headers.Cookie = cookie;
    }
   context.store.dispatch({
