import { Card, List } from "antd";
import axios from "axios";
import React from "react";
import { useSelector } from "react-redux";
import { END } from "redux-saga";
import { LOAD_POSTS_REQUEST } from "../../reducers/post";
import wrapper from "../../store/configureStore";
import Link from 'next/link';
import AppLayout from "../../components/AppLayout";
import Router from "next/router";

const AllPosts = () => {

    const {mainPosts} = useSelector((state) => state.post);

console.log("포스트", mainPosts);

const clickImage = (id) => {
    Router.push(`http://localhost:3000/post/${id}`);
};

            return(
                <AppLayout>
                <List 
                grid={{
                    gutter: 50,
                    column: 4
                }}
                dataSource={mainPosts}
                renderItem={(item) => (
                    <List.Item>
                        <Card 
                        title={<Link href={`/post/${item.id}`}>{item.lookName}</Link>}
                        
                        cover={<div onClick={clickImage(item.id)}><img style={{height: 250, width: '100%'}} alt="example" src={`http://localhost:3065/${item.Images[0]?.src}`} /></div>}
                        >
                        </Card>
                    </List.Item>
                )}
                />
                </AppLayout>
            );

};

export const getServerSideProps = wrapper.getServerSideProps(async(context) => { 
    
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = ''; 
    if(context.req && cookie){ 
        axios.defaults.headers.Cookie = cookie;
    }
    context.store.dispatch({
        type: LOAD_POSTS_REQUEST,
    });

   context.store.dispatch(END);
   await context.store.sagaTask.toPromise();
});

export default AllPosts;
