import { Card, Modal } from 'antd';
import { EditOutlined } from '@ant-design/icons';
import { useSelector } from 'react-redux';
import { useState } from 'react';
import { useRouter } from 'next/router';
const { Meta } = Card;


const Mention = () => {

  const {me} = useSelector((state) => state.user);

  const router = useRouter();
  const {id} = router.query;

  const [isModalOpen, setIsModalOpen] = useState(false);

  console.log("인용리스트", me);

  const referModal = () => {
    if(!id){
        return alert('로그인이 필요합니다.');
    }
    setIsModalOpen(true);
};

const handleCancel = () => {
  setIsModalOpen(false);
};

const IfRefer = () => {

    if(me.References[0] !== undefined){
       return <li>{me.References[0].content}</li>;
    }
    if(me.References[1] !== undefined){
      return <li>{me.References[1].content}</li>;
    }
    if(me.References[2] !== undefined){
      return <li>{me.References[2].content}</li>;
    }
    else{
      return  <div></div>;
    }

};

    return(
        <div>
            <Card
            title="I refered..."
            style={{
                width: 250, float: 'left', marginLeft: 17, marginTop: 30}}
                extra={<a onClick={referModal}>More</a>}
            >
              <IfRefer />
            </Card>
            <Modal title={me.nickname + " 의 인용 목록"} footer={null} onCancel={handleCancel} open={isModalOpen} >
              {me.References.map((refer) => (
                <ul key={refer.id}>
                  <li>{refer.content}</li>
                </ul>
              ))}
            </Modal>
        </div>
    );
};



export default Mention;
