import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSquare } from '@fortawesome/free-solid-svg-icons';
import { Card, Empty, List, Modal } from 'antd';
import { useState } from 'react';
import { useSelector } from 'react-redux';

const Followings = () => {

  const {me} = useSelector((state) => state.user);

  const [isModalOpen, setIsModalOpen] = useState(false);


  const referModal = () => {
    setIsModalOpen(true);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const IfFollowing = () => {

    if(me.Followings[0] !== undefined){
      return <li><FontAwesomeIcon icon={faSquare} style={{fontSize:15,}} /> {me.Followings[0].nickname}</li>;
    }
    if(me.Followings[1] !== undefined){
      return <li><FontAwesomeIcon icon={faSquare} style={{fontSize:15, color: 'pink'}} />{me.Followings[1].nickname}</li>;
    }
    if(me.Followings[2] !== undefined){
      return <li><FontAwesomeIcon icon={faSquare} style={{fontSize:15, color: 'pink'}} />{me.Followings[2].nickname}</li>;
    }
    else{
      return  <div>
                <Empty 
                description={
                  <span>
                    아직 팔로잉이 없어요.
                  </span>
                } 
                />
              </div>;
    }

};

const originalImage = `https://static.vecteezy.com/system/resources/previews/002/318/271/original/user-profile-icon-free-vector.jpg`;

    return(
        <div className="site-card-border-less-wrapper">
        <Card
          title="Followings"
          style={{width: 300, marginLeft: 20, height: 215}}
          extra={<a onClick={referModal}>More</a>}
        >
          <IfFollowing />

    </Card>
    <Modal title={me.nickname + " 의 팔로잉 목록"} footer={null} onCancel={handleCancel} open={isModalOpen}>
          
            <List
               grid={{
                gutter: 50,
                column: 3
            }}
              dataSource={me.Followings}
              renderItem={(item) => (
                <List.Item>
                    <Card 
                    title={<a href={`http://localhost:3000/post/${item.id}`} >{item.nickname}</a>}
                    cover={<a href={`http://localhost:3000/post/${item.id}`} ><img style={{width: '100%'}} alt="example" src={item.Image.src === originalImage ? originalImage : `http://localhost:3065/${item.Image.src}`} /></a>}
                    style={{width:110}}
                    >
                    </Card>
                </List.Item>
            )}
            >
                
            </List>

        </Modal>
      </div>
    );

};

export default Followings;
