import { HeartOutlined, InboxOutlined } from "@ant-design/icons";
import { Card, Form, Rate, Row, Upload } from "antd";
import TextArea from "antd/lib/input/TextArea";
import { useState } from "react";


const Comment = () => {

    const [fileList, setFileList] = useState([
       
      ]);
      const onChange = ({ fileList: newFileList }) => {
        setFileList(newFileList);
      };
    return(
        <div>
            <Card
              style={{
                width: 400,
                marginLeft: 720, height: 0
              }}
              
            >
                <Row><h2>Comment</h2></Row>
                <Row><h2>My rate is...</h2></Row>
               <Row><Rate character={<HeartOutlined />} value={3} /></Row>  
              <br />
              <Row><h2>My commnet is...</h2></Row>
              <br />
              <Row> <TextArea
                    showCount
                    
                    maxLength={250}
                    style={{
                        height: 110,
                        resize: 'none',
                        
                    }}
                    />
                </Row>
                <br />
                <Row><h2>I recommend this!</h2></Row>
                <Row><h5>아이템 추천은 2개까지 가능해요.</h5></Row>
                <Row>
               <Upload
               listType="picture-card"
               fileList={fileList}
               onChange={onChange}
               >
               {fileList.length < 2 && 'Drag images or click'}
               </Upload>
                </Row>
            </Card>
            
        </div>
    );
};

export default Comment;
