import { Button, Card, Col, Form, Input, Modal, Row, Select, Space, Switch, Tooltip, Upload  } from 'antd';
import { EditOutlined, HeartOutlined, HeartTwoTone, UploadOutlined  } from '@ant-design/icons';
import TextArea from "antd/lib/input/TextArea";
import AppLayout from '../../components/AppLayout';
import { FOLLOW_REQUEST, LOAD_MY_INFO_REQUEST, UNFOLLOW_REQUEST } from '../../reducers/user';
import { LIKE_POST_REQUEST, LOAD_POST_REQUEST, RETWEET_REQUEST, UNLIKE_POST_REQUEST } from '../../reducers/post';
import wrapper from '../../store/configureStore';
import { END } from 'redux-saga';
import { useRouter } from 'next/router';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import Look from '../look';
import NewComment from '../../components/newComment';
import { useCallback, useEffect, useState } from 'react';
import Comments from '../../components/comments';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faPenToSquare} from "@fortawesome/free-regular-svg-icons";
import {faHeartPulse} from "@fortawesome/free-solid-svg-icons";
import NoComments from '../../components/noComments';
import '../css/post[id].module.css';
import FollowButton from '../../components/FollowButton';
import useInput from '../../hooks/useInput';
const { Meta } = Card;
const { Option } = Select;

const Post = () => {

    // useEffect(() => {
    //     dispatch({
    //         type: LOAD_POST_REQUEST,
    //         data: context.params.id
    //     });
    // }, []);

    const dispatch = useDispatch();


    const router = useRouter();
    const {id} = router.query;
    const {userCalendarData, me, followLoading, unfollowLoading} = useSelector((state) => state.user);
    const {singlePost, retweetError} = useSelector((state) => state.post);

    const [index, setIndex] = useState(true);

    const [isModalOpen, setIsModalOpen] = useState(false);

    const [content, onChangeContent] = useInput('');

    console.log("singlePost::::: ", singlePost);

    const liked = singlePost.Likers[0];

    const isFollowing = me?.Followings.find((v) => v.id === singlePost.UserId);

    console.log("Ï¢ãÏïÑÏöî: ", liked);

    console.log("ÌÖçÏä§Ìä∏: ", content);


    //ÎåìÍ∏Ä Ïì∞Í∏∞, Î≥¥Í∏∞ ÏÑ†ÌÉù
    const onSelect = () => {
        setIndex(!index);
        console.log("index: ", index);
      };

      //Î¶¨Ìä∏Ïúó ÏóêÎü¨
      useEffect(() => {
        if(retweetError){
            window.alert(retweetError);
        }
      },[retweetError]);

      //Ï¢ãÏïÑÏöî
      const onLike = useCallback(() => {
        if(!id){
            return alert('Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.');
        }
       return dispatch({
                type: LIKE_POST_REQUEST,
                data: id
            });
        },[id]);

        //Ï¢ãÏïÑÏöî Ï∑®ÏÜå
        const onUnlike = useCallback(() => {
            if(!id){
                return alert('Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.');
            }
            return dispatch({
            type: UNLIKE_POST_REQUEST,
            data: id
           });
        },[id]);

        //ÌåîÎ°úÏö∞
        const onClickButton = useCallback(() => {
            if(isFollowing){
                dispatch({
                    type: UNFOLLOW_REQUEST,
                    data: singlePost.UserId
                }, window.alert("Ïñ∏ÌåîÎ°úÏö∞ Ìï©ÎãàÎã§."));
            }
            else{
                dispatch({
                    type: FOLLOW_REQUEST,
                    data: singlePost.UserId
                }, window.alert("ÌåîÎ°úÏö∞ Ìï©ÎãàÎã§."));
            }
        },[isFollowing]);

        //Î¶¨Ìä∏ÏúóÎ™®Îã¨
        const retweetModal = () => {
            if(!id){
                return alert('Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.');
            }
            setIsModalOpen(true);
        };

        //Î¶¨Ìä∏Ïúó
        const retweetSubmit = useCallback(() => {
            const formData = new FormData();

            formData.append('content', content);
            formData.append('postId', singlePost.id);

            dispatch({
                type: RETWEET_REQUEST,
                data: formData
            }, window.alert("Ïù∏Ïö© ÏÑ±Í≥µ!üîñ"), router.reload());
        }, [content]);
        
        //Î¶¨Ìä∏Ïúó Î™®Îã¨ Ï∞Ω Îã´Í∏∞
        const handleCancel = () => {
            setIsModalOpen(false);
          };

    //ÎåìÍ∏Ä ÏóÜÏùÑ Îïå
    const IfComments = () => {
        if(index === false){
            if(singlePost.Comments.length === 0){
                return (
                    <NoComments />
                );
            }else{
                <Comments post={singlePost} />
            }
        }else{
            return(
                null
            );
        }
    };


    return(
        <AppLayout >
        <div style={{width: 1120, position: 'relative' , zIndex: 1}}>
       <Row>
        <h1>DailyLook</h1>
       </Row>
       {me.id === singlePost.UserId 
            ?
            null
            :
            <Tooltip title="ÎÇ¥ Í≥ÑÏ†ïÏóê Ïù∏Ïö©ÌïòÍ∏∞">
            <FontAwesomeIcon 
                    style={{position: 'absolute', zIndex: 5, fontSize: 30, marginLeft: 200, marginTop: 10}} 
                    icon={faPenToSquare} 
                    onClick={retweetModal}

            
            />
            </Tooltip>
       }
      
            <Modal title="Ïù∏Ïö©ÌïòÍ∏∞" footer={null} onCancel={handleCancel} open={isModalOpen} >
                <Form  onFinish={retweetSubmit}>
                    <Input.TextArea 
                    value={content}
                    onChange={onChangeContent}
                    maxLength={200}
                    placeholder="Ïù∏Ïö© Î©îÎ™®Î•º ÎÇ®Í≤®Î≥¥ÏÑ∏Ïöî."
                    />
                    <img style={{marginLeft:120, marginTop: 20, height: 330}} src={`http://localhost:3065/${singlePost.Images[0].src}`} /> 
                    <Button style={{marginLeft: 384}} htmlType="submit">Ïù∏Ïö©ÌïòÍ∏∞</Button>
                </Form>
            </Modal>
            {/* <FontAwesomeIcon icon={faUserPlus} style={{position: 'absolute', zIndex: 5, fontSize: 30, marginLeft: 243, marginTop: 46}} /> */}
        {liked ? 
        <Tooltip title="Ï¢ãÏïÑÏöî Ï∑®ÏÜå">
        <HeartTwoTone onClick={onUnlike} twoToneColor="#fc83c6"  style={{position: 'absolute', zIndex: 5, fontSize: 30, marginLeft: 243, marginTop: 10}} /> 
        </Tooltip>
        :
        <Tooltip title="Ï¢ãÏïÑÏöî">
        <HeartOutlined onClick={onLike} style={{position: 'absolute', zIndex: 5, fontSize: 30, marginLeft: 243, marginTop: 10}} /> 
        </Tooltip>
        }
        {/* <FollowButton post={singlePost} /> */}
            <Col style={{float: 'left'}}>
                
                <Card
                style={{
                width: 280
                }}
                
                cover={<img style={{height: 550}} alt="example" src={`http://localhost:3065/${singlePost.Images[0].src}`} />}
                actions={[
                    id && singlePost.UserId === id
                    ? 
                    [
                    <EditOutlined key="edit" />,
                    <Upload>
                        <UploadOutlined />
                    </Upload>
                    ]
                    :
                    <Tooltip title={isFollowing ? 'Ïñ∏ÌåîÎ°úÏö∞' : 'ÌåîÎ°úÏö∞'}>
                        <FontAwesomeIcon
                        icon={faHeartPulse}
                        fontSize={20}
                        onClick={onClickButton}
                        />
                    </Tooltip>
                    
                ]}
                >      
                <Meta title={"Í∏ÄÏì¥Ïù¥:  " + singlePost.User.nickname} />
                </Card>
                <h1>This look is...{singlePost.rateAverage}points.</h1>
            </Col>
        <Col style={{float: 'left', width:500}}>
        <div>
            <ul>
            <li style={{display: 'inline-block'}}><h3 >Îç∞ÏùºÎ¶¨Î£© Ï†úÎ™©</h3></li>
            <li style={{display: 'inline-block', marginLeft:10}}><Input defaultValue={singlePost.lookName} readOnly /></li>
            </ul>

            </div>
            <div>
            <Card style={{marginLeft: 15, marginTop: 0,width:400}}>
            <Button style={{backgroundColor: 'black', color: 'white', marginBottom: 10}} block >Information</Button>
<thead>
    <tr >
    <th>Î∂ÑÎ•ò</th>
    <th>Î∏åÎûúÎìúÎ™Ö</th>
    
    </tr>
</thead>
<tbody style={{textAlign:'center'}} >
    <tr>
    <td> <Input disabled  defaultValue="ÏÉÅÏùò" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input defaultValue={singlePost.top} readOnly  /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="ÌïòÏùò" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input  defaultValue={singlePost.bottom} readOnly /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="ÏõêÌîºÏä§" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input  defaultValue={singlePost.dress} readOnly /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="ÏïÑÏö∞ÌÑ∞" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input  defaultValue={singlePost.outer} readOnly /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="Ïã†Î∞ú" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input defaultValue={singlePost.shoes} readOnly  /></td> 
    </tr>

    <tr>
    <td> <Input disabled  defaultValue="Ìå®ÏÖòÏû°Ìôî" style={{color: 'black', textAlign:'center'}} /></td>
    <td> <Input defaultValue={singlePost.acc} readOnly /></td> 
    </tr>

</tbody>
</Card>
</div>
<Card style={{ marginTop: 25, marginLeft: 15, width: 400, height: 210}}>
<Button style={{backgroundColor: 'black', color: 'white', marginBottom: 10}} block >Daily Record </Button>
<TextArea
  showCount
  maxLength={250}
  defaultValue={singlePost.content} 
  readOnly
  style={{
    height: 110,
    resize: 'none',
  }}
/>
</Card>
    { id && singlePost.UserId === id
    ?
        (
            <>
            <Button 
            style={{ left: '20%', marginTop: 5, backgroundColor: 'black', color: 'white'}}>
                ÏÇ≠Ï†úÌïòÍ∏∞
            </Button>
            <Button
            style={{ left: '30%', marginTop: 5, backgroundColor: 'black', color: 'white'}}>
                ÏàòÏ†ïÌïòÍ∏∞
            </Button>
            </>
        )
        :
        null
    }



</Col>

<Col>
    {/* ÎåìÍ∏Ä */}
    <Switch defaultChecked={index} onChange={onSelect} style={{marginLeft: 233}} checkedChildren="ÎåìÍ∏ÄÏì∞Í∏∞" unCheckedChildren="ÎåìÍ∏ÄÎ≥¥Í∏∞" />
    {index === true  ? <NewComment post={singlePost} /> : null }
    {index === false  ? ( singlePost.Comments.length !== 0 ? <Comments post={singlePost} /> : <NoComments />) : null }
    {/* <IfComments /> */}
</Col>

</div>
    </AppLayout>
    );

};

export const getServerSideProps = wrapper.getServerSideProps(async(context) => { 
    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = ''; 
    if(context.req && cookie){ 
        axios.defaults.headers.Cookie = cookie;
    }
   context.store.dispatch({
        type: LOAD_MY_INFO_REQUEST
    });
    context.store.dispatch({
        type: LOAD_POST_REQUEST,
        data: context.params.id //ÌòπÏùÄ context.query.id
    });
   context.store.dispatch(END);
   await context.store.sagaTask.toPromise();
});


export default Post;
