import React, { useCallback, useState } from "react";
import {Input, Menu, Row, Col, Button, Tooltip, AutoComplete} from 'antd';
import Link from 'next/link';
import useInput from "../hooks/useInput";
import Router from "next/router";
import { useDispatch, useSelector } from "react-redux";
import { logoutRequestAction } from "../reducers/user";
import { SEARCH_KEYWORD_REQUEST } from "../reducers/post";


const AppLayout = ({children}) => {

    const dispatch = useDispatch();

    const [keyword, onChangeKeyword] = useInput('');
    // const [searchInput, onChangeSearchInput] = useInput('');
    const {me, logOutLoading} = useSelector((state) => state.user);

    const {keywords} = useSelector((state) => state.post);

    const [value, setValue] = useState('');

    const onChangeSearchInput = useCallback(() => {

        setValue(e.target.value);

        const formData = new FormData();

        formData.append('keyword', keyword);
        dispatch({
            type: SEARCH_KEYWORD_REQUEST,
            data: formData
        });
    });

    const onSearch = useCallback(() => {
        Router.push(`/hashtag/${value}`);
      }, [value]);

    const onLogOut = useCallback(() => {
    dispatch(logoutRequestAction());
    Router.replace(`/`);
    }, []);

    return(
        <div >
               <Menu id="menu1" mode='horizontal'>    
               
                <Menu.Item >
                <Link href="/"><h1 className="item1" >FASHIONARY</h1></Link>
                </Menu.Item>
                <Menu.Item>
                <Link href="/profile"><h3 className="item1" >My Profile</h3></Link>
                </Menu.Item >
                <Menu.Item>
                <Link href="/post/allPosts"><h3 className="item1" >All Looks</h3></Link>
                </Menu.Item> 
                <Menu.Item className="item2">
                    <Tooltip title='찾고 싶은 브랜드를 검색해보세요.'>
                    
                    <AutoComplete 
                        style={{marginTop: 12}}
                        value={value}
                        onChange={onChangeSearchInput}
                        onSearch={onSearch}
                        options={keywords}
                    />
                    <Input.Search 
                    // style={{marginTop: 12}}
                    // value={searchInput}
                    // onChange={onChangeSearchInput}
                    onSearch={onSearch}
                    />
                    </Tooltip>
                </Menu.Item>
                <Menu.Item>
                <h4 className="item1">{ me ? "현재 로그인 계정: " + me.nickname : null}</h4>
                </Menu.Item>
                { me 
                ? 
                <Menu.Item>
                    <Button className="button1" onClick={onLogOut} loading={logOutLoading} >로그아웃</Button>
                </Menu.Item>
                :
                null
            
                }
            </Menu>
            <Row gutter={8}>
                <Col xs={24} md={6}>
                </Col>
                <Col xs={24} md={12}>{children}</Col>
            </Row>
            
        </div>
    );

};

export default AppLayout;
