import React, { useCallback, useState } from "react";
import { LockOutlined, UserOutlined } from '@ant-design/icons';
import { Button, Card, Checkbox, Form, Input } from 'antd';
import useInput from "../hooks/useInput";
import AppLayout from "../components/AppLayout";
import styled from 'styled-components';
import Head from 'next/head';

const ErrorMessage = styled.div`
    color: red;
`;

const Signup = () => {

  const[ email, onChangeEmail ] = useInput('');
  const [nickname, onChangeNickname] = useInput('');
  const [password, onChangePassword] = useInput('');
  const [passwordCheck, setPasswordCheck] = useState('');
  const [passwordError, setPasswordError] = useState(false);
  const [termError, setTermError] = useState(false);
  const [term, setTerm] = useState('');

  const onChangePasswordCheck = useCallback((e) => {
      setPasswordCheck(e.target.value);
      setPasswordError(e.target.value !== password);
  }, [password]);

  const onChangeTerm = useCallback((e) => {
      setTerm(e.target.checked);
      setTermError(false);
  },[]);    

  const onSubmit = useCallback(() => {
      if(password !== passwordCheck){
          return setPasswordError(true);
      }
      if(!term){
          return setTermError(true);
      }
      console.log(email, nickname, password);
      dispatch({
          type: SIGN_UP_REQUEST,
          data: {email, password, nickname}
      });
  }, [email, password, passwordCheck, term]);

return(
  <AppLayout>
     <Head>
            <title>회원가입</title>
      </Head>

    <Card style={{width: '60%', margin: 'auto', marginTop: '300px'}}>
    
    <Form
    name="normal_login"
    className="login-form"
    initialValues={{
      remember: true,
    }}
    onFinish={onSubmit}
    style={{width: '50%', textAlign : 'center', margin: 'auto'}}
  >
    <Form.Item
      name="username"
    >
      <label htmlFor='name'>닉네임</label>
      <Input prefix={<UserOutlined className="site-form-item-icon" />} 
      name="name"
      placeholder="Username" 
      value={nickname} 
      onChange={onChangeNickname}
      required
      />
    </Form.Item>
    <Form.Item
      name="password"
    >
      <label htmlFor='password'>비밀번호</label>
      <Input
        prefix={<LockOutlined className="site-form-item-icon" />}
        name="password"
        placeholder="Password"
        value={password} 
        type="password"
        onChange={onChangePassword}
        required
      />
    </Form.Item>

    <Form.Item>
    <label htmlFor='password-check'>비밀번호</label>
      <Input
        prefix={<LockOutlined className="site-form-item-icon" />}
        name='password-check'
        placeholder="Check Password"
        type="password"
        value={passwordCheck} 
        onChange={onChangePasswordCheck}
         required />
         {passwordError && <ErrorMessage>비밀번호가 일치하지 않습니다.</ErrorMessage>}
    </Form.Item>

    <Form.Item>
    <Checkbox name='user-term' checked={term} onChange={onChangeTerm}>동의합니다.</Checkbox>
            {termError && <ErrorMessage>약관에 동의하셔야 합니다.</ErrorMessage>}
    </Form.Item>

    <Form.Item>
      <Button style={{backgroundColor: 'black', color: 'white'}} htmlType="submit" className="login-form-button">
        회원가입 하기
      </Button>
      
    </Form.Item>
  </Form>
  </Card>
  </AppLayout>
);
};

export default Signup;
