import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { Card, Modal } from 'antd';
import { useState } from 'react';
import { useSelector } from 'react-redux';

const LikeList = () => {

  const {me} = useSelector((state) => state.user);

  const [isModalOpen, setIsModalOpen] = useState(false);

  console.log("인용리스트", me);

  const referModal = () => {

    setIsModalOpen(true);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const IfLike = () => {

      if(me.Liked[0] !== undefined){
        return <li><FontAwesomeIcon icon={faHeart} style={{fontSize:15, color: 'pink'}} /> {me.Liked[0].lookName}</li>;
      }
      if(me.Liked[1] !== undefined){
        return <li><FontAwesomeIcon icon={faHeart} style={{fontSize:15, color: 'pink'}} />{me.Liked[1].lookName}</li>;
      }
      if(me.Liked[2] !== undefined){
        return <li><FontAwesomeIcon icon={faHeart} style={{fontSize:15, color: 'pink'}} />{me.Liked[2].lookName}</li>;
      }
      else{
        return  <div></div>;
      }

  };


    return(
        <div className="site-card-border-less-wrapper">
        <Card
          title="I like this!"
          style={{width: 300, height: 350}}
          extra={<a onClick={referModal}>More</a>}
        >
          <IfLike />
    </Card>
    <Modal title={me.nickname + " 의 좋아요 목록"} footer={null} onCancel={handleCancel} open={isModalOpen}>
      {me.Liked.map((love) => (
        <ul key={love.id}>
          <p><FontAwesomeIcon icon={faHeart} style={{fontSize:15, color: 'pink'}} /> <img style={{width: 60}} src={`http://localhost:3065/${love.Images[0].src}`} /></p>
        </ul>
      ))}
    </Modal>
      </div>
    );

};

export default LikeList;
