            case diaryMoment: 
              if(diaryMoment === moment(mainPosts[i].createdAt).format("YYYY-MM-DD")){
            calendarData = [
               {
                  photo: mainPosts[i].Images[0].src,
                  title: mainPosts[i].lookName,
                  date: diaryMoment,
                  id: mainPosts[i].id
               }
