import React, { useCallback, useEffect, useRef, useState } from "react";
import '../pages/css/myInfo.module.css';
import { Button, Card, Form, Input, Tooltip } from 'antd';
import Router from 'next/router';
import { EditOutlined } from '@ant-design/icons';
import useInput from "../hooks/useInput";
import { useDispatch, useSelector } from "react-redux";
import { CHANGE_INFO_REQUEST, UPLOAD_PROFILE_IMAGE_REQUEST } from "../reducers/user";
const { Meta } = Card;

const MyInfo = () => {

  const dispatch = useDispatch();

  const {me, changeInfoDone, imagePaths} = useSelector((state) => state.user);

  const [previewImg, setPreviewImg] = useState(null);

  const [read, setRead] = useState(true);

  const [text, onChangeText, setText] = useInput('');

  const imageInput = useRef(); 

  useEffect(() => {
    if(changeInfoDone){
      Router.replace('/');
    }
  }, [changeInfoDone]);

  const onClickImageUpload = useCallback(() => {
    imageInput.current.click();
    setRead(false);
}, [imageInput.current]);

// const onChangeImages = useCallback((e) => {
//   console.log('images', e.target.files);

//   let reader = new FileReader();
//   if(e.target.files[0]){
//     reader.readAsDataURL(e.target.files[0])
//   }
//   reader.onloadend = () => {
//     const previewImgUrl = reader.result
//     if(previewImgUrl){
//       setPreviewImg(previewImgUrl)
//     }
//   }
// );

//유저 프로필 이미지 변경, 이미지 미리보기
  const onChangeImages = useCallback((e) => {
    console.log('image:::', e.target.files);

    const reader = new FileReader();
    if(e.target.files[0]){
      reader.readAsDataURL(e.target.files[0]);
    }
    reader.onloadend = () => {
      const previewImgUrl = reader.result;
      if(previewImgUrl){
        setPreviewImg(previewImgUrl);
      }
    }

    const imageFormData = new FormData();
   
    [].forEach.call(e.target.files, (f) => {
      imageFormData.append('profile', f);
  });

   return dispatch({
        type: UPLOAD_PROFILE_IMAGE_REQUEST,
        data: imageFormData
    });
});

const onSubmit = useCallback(() => {
  if(!text || !text.trim()){
      return alert('자기소개를 작성하세요.');
  }
  const formData = new FormData();
  
      formData.append('profile', imagePaths);
  
  formData.append('info', text);
  console.log('text: ' + text);
  dispatch({ 
      type: CHANGE_INFO_REQUEST,
      data: formData,
   });
},[text, imagePaths]);

// console.log("me:::" +JSON.stringify(me));

    return(
        <div>
          <Form encType="multipart/form-data" onFinish={onSubmit}>
      <Card
        title="My Info"
        style={{marginLeft: 15, width: 250}}
        cover={
          // me
          // ?
          <img
            alt="example"
            src={previewImg ? previewImg : `http://localhost:3065/${me.Image.src}`}
           ref={imageInput}
            height='300'
          />
          // :
          // <img src={`http://localhost:3065/${me.Image.src}`}  />
          
              }
        
    actions={[
      <Tooltip placement="bottom" title='클릭하면 프로필 이미지와 자기소개를 수정할 수 있어요.'>
      <Button 
      onClick={onClickImageUpload}
      ><EditOutlined key="edit" /></Button>
      </Tooltip>,
      <Button htmlType="submit">save</Button>
    ]}
  >
    <input name="profile" type="file" hidden ref={imageInput} onChange={onChangeImages} />
    <Meta
      title={<Input  readOnly={read} defaultValue={me.nickname} />}
      description={
        read
        ?
        <Input  readOnly={read} defaultValue={me.info} />
        :
        <Input  readOnly={read} placeholder={me.info} onChange={onChangeText} value={text} /> 
      }
    />
  </Card>
  </Form>
        </div>
    );

};

export default MyInfo;
