import { HeartOutlined, InboxOutlined } from "@ant-design/icons";
import { Button, Card, Form, Rate, Row, Upload } from "antd";
import TextArea from "antd/lib/input/TextArea";
import { useCallback, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import useInput from "../hooks/useInput";
import { ADD_COMMENT_REQUEST, REMOVE_IMAGE, UPLOAD_IMAGES_REQUEST } from "../reducers/post";


const Comments = ({post}) => {

  const dispatch = useDispatch();


    const [fileList, setFileList] = useState([]);

    const id = useSelector((state) => state.user.me?.id);

    const [value, setValue] = useState({});

    const [ content, onChangeContent, setContent ] = useInput('');

    console.log("댓글: ", post.Comments);

    const handleRate = (value) => {
      setValue(value);
      console.log("점수:::" + value);
    };

      //미리보기
      const onPreview = async (file) => {
        let src = file.url;
        if (!src) {
          src = await new Promise((resolve) => {
            const reader = new FileReader();
            reader.readAsDataURL(file.originFileObj);
            reader.onload = () => resolve(reader.result);
          });
        }
        const image = new Image();
        image.src = src;
        const imgWindow = window.open(src);
        imgWindow?.document.write(image.outerHTML);
      };


    return(
        <div>
          <Form encType="multipart/form-data" >
            <Card
              style={{
                width: 400,
                marginLeft: 720, height: 0
              }}
              
            >
                <Row><h2>Comment</h2></Row>
                <Row><h2>My rate is...</h2></Row>
               <Row><Rate style={{fontSize: 50}} onChange={handleRate} value={post.Comments.rate} /></Row>  
              <br />
              <Row><h2>My commnet is...</h2></Row>
              
              <Row> <TextArea
                    showCount
                    maxLength={250}
                    value={content}
                    onChange={onChangeContent}
                    style={{
                        height: 110,
                        resize: 'none',
                        
                    }}
                    />
                </Row>
                <br />
                <Row><h2>I recommend this!</h2></Row>
                {/* <Row><h3>아이템 추천은 2개까지 가능해요.</h3></Row> */}
                <Row>
               <Upload
               listType="picture-card"
               fileList={fileList}
               onPreview={onPreview}
               >
               </Upload>
                </Row>
                <Button >댓글 삭제</Button>
            </Card>
          </Form>
        </div>
    );
};

export default Comments;
