import { CaretLeftOutlined, CaretRightOutlined, HeartOutlined, InboxOutlined } from "@ant-design/icons";
import { Button, Card, Form, Rate, Row, Upload } from "antd";
import TextArea from "antd/lib/input/TextArea";
import { useCallback, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import useInput from "../hooks/useInput";
import { ADD_COMMENT_REQUEST, REMOVE_IMAGE, UPLOAD_IMAGES_REQUEST } from "../reducers/post";
import Score from "./score";


const Comments = ({post}) => {

  const dispatch = useDispatch();


    const [fileList, setFileList] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [postsPerPage] = useState(1);

    const id = useSelector((state) => state.user.me?.id);

    const [value, setValue] = useState({});

    const [ content, onChangeContent, setContent ] = useInput('');

    console.log("댓글: ", post.Comments);

    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentComments = post.Comments.slice(indexOfFirstPost, indexOfLastPost);

    const pageNumbers = [];

    console.log("total opinions " + opinionList.opinions.length);
    console.log("postsPerPage " + postsPerPage);

    for (let i = 1; i <= Math.ceil(post.Comments.length / postsPerPage); i++) {
        pageNumbers.push(i);
        }

    const handleRate = (value) => {
      setValue(value);
      console.log("점수:::" + value);
    };

        //댓글 다음 페이지로 이동
        const upClick = () => {
            if(currentPage < pageNumbers.length){
              setCurrentPage(currentPage + 1);
            } else {
              alert("더 이상 댓글이 없어요.");
            };
          };
      
          //댓글 이전 페이지로 이동
          const downClick = () => {
            if(currentPage > 1){
              setCurrentPage(currentPage - 1);
            } else {
              alert("가장 최신 댓글이에요.");
            };
          };

      //미리보기
      const onPreview = async (file) => {
        let src = file.url;
        if (!src) {
          src = await new Promise((resolve) => {
            const reader = new FileReader();
            reader.readAsDataURL(file.originFileObj);
            reader.onload = () => resolve(reader.result);
          });
        }
        const image = new Image();
        image.src = src;
        const imgWindow = window.open(src);
        imgWindow?.document.write(image.outerHTML);
      };


    return(
        <div>
          <Form encType="multipart/form-data" >
            <Card
              style={{
                width: 400,
                marginLeft: 720, height: 0
              }}
            >

                <Row><h2>Comment</h2> <CaretLeftOutlined onClick={downClick} /> <CaretRightOutlined onClick={upClick} /></Row>
                <Row><h2>My rate is...</h2></Row>
               <Row>
                <Score comments={currentComments} />
                {/* <Rate style={{fontSize: 50}} onChange={handleRate} value={post.Comments.rate} /> */}
                </Row>  
              <br />
              <Row><h2>My commnet is...</h2></Row>
              
              <Row> <TextArea
                    showCount
                    maxLength={250}
                    value={content}
                    onChange={onChangeContent}
                    style={{
                        height: 110,
                        resize: 'none',
                        
                    }}
                    />
                </Row>
                <br />
                <Row><h2>I recommend this!</h2></Row>
                {/* <Row><h3>아이템 추천은 2개까지 가능해요.</h3></Row> */}
                <Row>
               <Upload
               listType="picture-card"
               fileList={fileList}
               onPreview={onPreview}
               >
               </Upload>
                </Row>
                <Button >댓글 삭제</Button>
            </Card>
          </Form>
        </div>
    );
};

export default Comments;
