import { CaretLeftOutlined, CaretRightOutlined, HeartOutlined, InboxOutlined } from "@ant-design/icons";
import { Button, Card, Form, Rate, Row, Upload } from "antd";
import { useCallback, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import Score from "./score";


const Comments = ({post}) => {

  const dispatch = useDispatch();


    const [fileList, setFileList] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [postsPerPage] = useState(1);

    const id = useSelector((state) => state.user.me?.id);

    const [value, setValue] = useState({});

    console.log("댓글: ", post.Comments);

    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentComments = post.Comments.slice(indexOfFirstPost, indexOfLastPost);

    const pageNumbers = [];

    console.log("total opinions " + post.Comments.length);
    console.log("postsPerPage " + postsPerPage);

    for (let i = 1; i <= Math.ceil(post.Comments.length / postsPerPage); i++) {
        pageNumbers.push(i);
        }

    const handleRate = (value) => {
      setValue(value);
      console.log("점수:::" + value);
    };

        //댓글 다음 페이지로 이동
        const upClick = () => {
            if(currentPage < pageNumbers.length){
              setCurrentPage(currentPage + 1);
            } else {
              alert("더 이상 댓글이 없어요.");
            };
          };
      
          //댓글 이전 페이지로 이동
          const downClick = () => {
            if(currentPage > 1){
              setCurrentPage(currentPage - 1);
            } else {
              alert("가장 최신 댓글이에요.");
            };
          };

      //미리보기
      const onPreview = async (file) => {
        let src = file.url;
        if (!src) {
          src = await new Promise((resolve) => {
            const reader = new FileReader();
            reader.readAsDataURL(file.originFileObj);
            reader.onload = () => resolve(reader.result);
          });
        }
        const image = new Image();
        image.src = src;
        const imgWindow = window.open(src);
        imgWindow?.document.write(image.outerHTML);
      };


    return(
        <div>
          <Form encType="multipart/form-data" >
            <Card
              style={{
                width: 400,
                marginLeft: 720, height: 0
              }}
            >
                <Row>
                    <h2>Comment</h2>
                     <CaretLeftOutlined onClick={downClick} />
                      <CaretRightOutlined onClick={upClick} /> 
                      {currentPage} / {post.Comments.length} 
                </Row>
                <Score comments={currentComments} />
                <Button >댓글 삭제</Button>
            </Card>
          </Form>
        </div>
    );
};

export default Comments;
