import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSquare } from '@fortawesome/free-regular-svg-icons';
import { Card, Empty, List, Modal } from 'antd';
import { useState } from 'react';
import { useSelector } from 'react-redux';
import styled from 'styled-components';

const Li = styled.li`
  .FontAwesomeIcon{
    font-size: 15px;
    color: 'pink';
  }
`;

const Followers = () => {

  const {me} = useSelector((state) => state.user);

  const [isModalOpen, setIsModalOpen] = useState(false);

  console.log("팔로워리스트", me);

  const referModal = () => {
    setIsModalOpen(true);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const IfFollower = () => {

      if(me.Followers[0] !== undefined){
        return <Li><FontAwesomeIcon icon={faSquare} className={ficon} /> {me.Followers[0].nickname}</Li>;
      }
      if(me.Followers[1] !== undefined){
        return <Li><FontAwesomeIcon icon={faSquare} />{me.Followers[1].nickname}</Li>;
      }
      if(me.Followers[2] !== undefined){
        return <Li><FontAwesomeIcon icon={faSquare} />{me.Followers[2].nickname}</Li>;
      }
      else{
        return  <div>
                    <Empty 
                    description={
                      <span>
                        아직 팔로워가 없어요.
                      </span>
                    } 
                    />
                </div>;
      }

  };

  const originalImage = `https://static.vecteezy.com/system/resources/previews/002/318/271/original/user-profile-icon-free-vector.jpg`;

    return(
      <div className="site-card-border-less-wrapper">
        <Card
          title="Followers"
          style={{width: 300, marginLeft: 50, height: 215}}
          extra={<a onClick={referModal}>More</a>}
        >
          <IfFollower />
        
        </Card>
        <Modal title={me.nickname + " 의 팔로워 목록"} footer={null} onCancel={handleCancel} open={isModalOpen}>
         

            <List
               grid={{
                gutter: 50,
                column: 3
              }}
              dataSource={me.Followers}
              renderItem={(item) => (
                <List.Item>
                    <Card
                    title={<a href={`http://localhost:3000/post/${item.id}`} >{item.nickname}</a>}
                    cover={<a href={`http://localhost:3000/post/${item.id}`} ><img style={{width: '100%'}} alt="example" src={item.Image.src === originalImage ? originalImage : `http://localhost:3065/${item.Image.src}`} /></a>}
                    style={{width:110}}
                    >
                    </Card>
                </List.Item>
            )}
            >
          </List> 
        </Modal>
      </div>
    );

};

// {me.Followers.map((follower) => (
//   <ul key={follower.id}>
//     <p><FontAwesomeIcon icon={faSquare} style={{fontSize:15, color: 'pink'}} /> 
//     <img style={{width: 60}} src={following.Image.src === originalImage ? originalImage : `http://localhost:3065/${follower.Image.src}`} />
//     </p>
//   </ul>
//   ))}

export default Followers;
