import React, { createElement, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import moment from "moment";
import Link from 'next/link';
import { LOAD_USER_CALENDAR_DATA_REQUEST } from "../reducers/user";

const DateCellRender = (date) => {

   const dispatch = useDispatch();

   const {userCalendarData, me} = useSelector((state) => state.user);

   useEffect(() => {
      dispatch({
         type: LOAD_USER_CALENDAR_DATA_REQUEST,
         data: me.id
      });
   }, []);

   console.log("UserCalendarData:::::::::" + JSON.stringify(userCalendarData));
   console.log("UserCalendarData길이: ", userCalendarData.length);
   const now = new Date();
   console.log("NEwDate: ", now.getDate());
 //  console.log("Render:::::::::" + JSON.stringify(mainPosts[0].Images[0].src));

   const getDayInfo = () => {
      const calendarData = [];
      let diaryMoment;

      for( let i = 0; i < userCalendarData.length; i++ ){
         console.log("생성 날짜",  moment(userCalendarData[i].createdAt).format("YYYY-MM-DD"));
         diaryMoment = moment(userCalendarData[i].createdAt).format("YYYY-MM-DD");

         if(diaryMoment){
            calendarData.push( {
               photo: userCalendarData[i].Images[0].src,
               title: userCalendarData[i].lookName,
               date: diaryMoment,
               id: userCalendarData[i].id
            });
              
            console.log("이미지???", userCalendarData[i].Images[0].src);
         }
      }

      console.log("calendarData:::::" + JSON.stringify(calendarData));
      return calendarData || [];
   };

   const PhotoRander = ({date, photo}) => {

      switch(new Date(date).getDate()){
         case new Date().getDate() :  
         return (
            <Link href="#">
                <img style={{width: 50}} src={`http://localhost:3065/${photo}`} />
            </Link>
         );
         default:
         return(
            <div></div>
         );   
      }

      if(photo && title){
         return(
            <>
            <h5>{title}</h5>
            <Link href="#">
         <img style={{width: 50}} src={`http://localhost:3065/${photo}`} />
         </Link>
         </>
         );
      }
      else{
         return(
            <div></div>
         );
      }

   };

   const NameRander = ({date, name}) => {

      switch(new Date(date).getDate()){
         case new Date().getDate() : 
         return(
            <h5>{name}</h5>
         );
         default:
         return(
            <div></div>
         );   
      }

   };

   const dayInfo = getDayInfo();

  //const photo = `http://localhost:3065/sean-kong-1dJmkdVuelg-unsplash_1669727155305.jpg`;

 return(
  <div>
   {dayInfo.map((info) => (

         <div key={info.id} >
            {/* <LookRander date={info.date} photo={info.photo} title={info.title} />  */}
              {/* <h5>{info.title}</h5>
            <Link href="#">
         <img style={{width: 50}} src={`http://localhost:3065/${info.photo}`} />
         </Link> */}
         <NameRander  name={info.title} />
         <PhotoRander  photo={info.photo} />
         </div>

      
   ))}
  </div>
 );
};

export default DateCellRender;
