import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { Card, Empty, Modal } from 'antd';
import { useState } from 'react';
import { useSelector } from 'react-redux';
import styled from 'styled-components';

const One = styled.div`
  .icons{
    font-size:15px;
    color: 'pink' !important ;
  }
  .card1{
    width: 300px; 
    height: 215px;
  }
  .img1{
    width: 60px;
  }
`;

const LikeList = () => {

  const {me} = useSelector((state) => state.user);

  const [isModalOpen, setIsModalOpen] = useState(false);

  console.log("인용리스트", me);

  const referModal = () => {
    setIsModalOpen(true);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };

  const IfLike = () => {

      if(me.Liked[0] !== undefined){
        return <li><FontAwesomeIcon icon={faHeart} id='icons' /> {me.Liked[0].lookName}</li>;
      }
      if(me.Liked[1] !== undefined){
        return <li><FontAwesomeIcon icon={faHeart} className='icons' />{me.Liked[1].lookName}</li>;
      }
      if(me.Liked[2] !== undefined){
        return <li><FontAwesomeIcon icon={faHeart} className='icons' />{me.Liked[2].lookName}</li>;
      }
      else{
        return  <div>
                  <Empty
                  description={
                    <span>
                      아직 좋아요를 한 Look이 없어요.
                    </span>
                  } 
                  />
                </div>;
      }
  
  };


    return(
        <div className="site-card-border-less-wrapper">
          <One>
            <Card
              title="I like this!"
              className='card1'
              extra={<a onClick={referModal}>More</a>}
            >
              <IfLike />
            </Card>
            <Modal title={me.nickname + " 의 좋아요 목록"} footer={null} onCancel={handleCancel} open={isModalOpen}>
              {me.Liked.map((love) => (
                <ul key={love.id}>
                 <One> <p><FontAwesomeIcon icon={faHeart} className='icons'/> <img className='img1' src={`http://localhost:3065/${love.Images[0].src}`} /></p></One>
                </ul>
              ))}
            </Modal>
        </One>
      </div>
    );

};

export default LikeList;
