import React, { useCallback, useEffect, useRef, useState } from "react";
import '../pages/css/myInfo.module.css';
import { Button, Card, Form, Input, Tooltip } from 'antd';
import { EditOutlined } from '@ant-design/icons';
import useInput from "../hooks/useInput";
import { useDispatch, useSelector } from "react-redux";
import { UPLOAD_IMAGES_REQUEST } from "../reducers/post";
import { CHANGE_INFO_REQUEST } from "../reducers/user";
const { Meta } = Card;

const MyInfo = () => {

  const dispatch = useDispatch();

  const {me, changeInfoDone} = useSelector((state) => state.user);

  const [previewImg, setPreviewImg] = useState(null);

  const [read, setRead] = useState(true);

  const {imagePaths, addPostDone} = useSelector((state) => state.post);

  const [text, onChangeText, setText] = useInput('');

  const imageInput = useRef(); 

  useEffect(() => {
    if(changeInfoDone){
      replace('/');
    }
  }, [changeInfoDone]);

  const onClickImageUpload = useCallback(() => {
    imageInput.current.click();
    setRead(false);
}, [imageInput.current]);

// const onChangeImages = useCallback((e) => {
//   console.log('images', e.target.files);

//   let reader = new FileReader();
//   if(e.target.files[0]){
//     reader.readAsDataURL(e.target.files[0])
//   }
//   reader.onloadend = () => {
//     const previewImgUrl = reader.result
//     if(previewImgUrl){
//       setPreviewImg(previewImgUrl)
//     }
//   }
// );

  const onChangeImages = useCallback((e) => {
    console.log('images', e.target.files);
    const imageFormData = new FormData();
    [].forEach.call(e.target.files, (f) => {
        imageFormData.append('image', f);
    });
    console.log('imageFormData: ' + imageFormData);
   return dispatch({
        type: UPLOAD_IMAGES_REQUEST,
        data: imageFormData
    });
});

const onSubmit = useCallback(() => {
  if(!text || !text.trim()){
      return alert('자기소개를 작성하세요.');
  }
  const formData = new FormData();
  imagePaths.forEach((p) => {
      formData.append('image', p);
  });
  formData.append('content', text);
  dispatch({
      type: CHANGE_INFO_REQUEST,
      data: formData,
   });
},[text, imagePaths]);


    return(
        <div>
          <Form encType="multipart/form-data" onFinish={onSubmit}>
      <Card
        title="My Info"
        style={{marginLeft: 15, width: 300}}
        cover={
          me.Images
          ?
          me.Images[0]
          :
          <img
            alt="example"
            src={imageInput ? imageInput : 'https://static.vecteezy.com/system/resources/previews/002/318/271/original/user-profile-icon-free-vector.jpg'}
           ref={imageInput}
            height='250px'
          />
              }
        
    actions={[
      <Tooltip placement="bottom" title='클릭하면 프로필 이미지와 자기소개를 수정할 수 있어요.'>
      <Button 
      onClick={onClickImageUpload}
      ><EditOutlined key="edit" /></Button>
      </Tooltip>,
      <Button htmlType="submit">save</Button>
    ]}
  >
    <input type="file" hidden ref={imageInput} onChange={onChangeImages} />
    <Meta
      title={<Input name="nickname" readOnly={read} defaultValue={me.nickname} />}
      description={<Input name="description" readOnly={read} defaultValue={me.info} onChange={onChangeText} value={text} /> }
    />
  </Card>
  </Form>
        </div>
    );

};

export default MyInfo;
