import React, { useCallback, useEffect } from "react";
import { LockOutlined, UserOutlined } from '@ant-design/icons';
import { Button, Card, Checkbox, Form, Input } from 'antd';
import { useDispatch, useSelector } from "react-redux";
import useInput from '../hooks/useInput';
import { loginRequestAction } from "../reducers/user";
import  Router  from "next/router";

const Login = () => {

  const dispatch = useDispatch();
  const {loginLoading, logInError, logInDone} = useSelector((state) => state.user);
  const [nickname, onChangeNickname] = useInput('');
  const [password, onChangePassword] = useInput('');

  useEffect(() => {
      if(logInError){
          alert(logInError);
      }
  }, [logInError]);

  // const style = useMemo(() => ({marginTop: 10}), []);

  const onSubmitForm = useCallback(() => {
      console.log(nickname, password);
      dispatch(loginRequestAction({nickname, password}));
      if(logInDone === true){
        Router.replace('/');
      }
  }, [nickname, password]);


    return(
        <Card style={{width: '60%', margin: 'auto', marginTop: '300px'}} >
        <Form
        name="normal_login"
        className="login-form"
        style={{width: '50%', textAlign : 'center', margin: 'auto'}}
        onFinish={onSubmitForm}
        >
        <Form.Item
          name="username"
        >
          <Input value={nickname} onChange={onChangeNickname} prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Username" />
        </Form.Item>
        <Form.Item
          name="password"
        >
          <Input
            prefix={<LockOutlined className="site-form-item-icon" />}
            type="password"
            value={password} 
            onChange={onChangePassword}
            placeholder="Password"
          />
        </Form.Item>

    
        <Form.Item>
          <Button loading={loginLoading} style={{backgroundColor: 'black', color: 'white'}} htmlType="submit" className="login-form-button">
            로그인
          </Button>
          <br />
          Or 
          <br />
          <a href="./signup" style={{color: 'black'}}>회원가입 하러 가기</a>
        </Form.Item>
      </Form>
      </Card>
    );

}

export default Login;
