import AppLayout from "../components/AppLayout";
import { Button, Card, Carousel, Col, Form, Input, Row, Select, Space, Upload  } from 'antd';
import { EditOutlined, UploadOutlined  } from '@ant-design/icons';
import TextArea from "antd/lib/input/TextArea";
import { useDispatch, useSelector } from "react-redux";
import { useCallback, useEffect, useRef, useState } from "react";
import useInput from "../hooks/useInput";
import { ADD_POST_REQUEST, LOAD_POSTS_REQUEST, REMOVE_IMAGE, UPLOAD_IMAGES_REQUEST } from "../reducers/post";
import Login from "./login";
import { useRouter } from "next/router";
import Router from "next/router";
const { Meta } = Card;
const { Option } = Select;

const NewLook = () => {

    const dispatch = useDispatch();

    const {me} = useSelector((state) => state.user);


    const imageInput = useRef();

    // const top = useRef();
    // const bottom = useRef();
    // const dress = useRef();
    // const shoes = useRef();
    // const acc = useRef();
    // const outer = useRef();
    // const lookName = useRef();

    const [top, onChangeTop ,setTop] = useInput('');
    const [bottom, onChangeBottom ,setBottom] = useInput('');
    const [dress, onChangeDress ,setDress] = useInput('');
    const [shoes, onChangeShoes ,setShoes] = useInput('');
    const [acc, onChangeAcc ,setAcc] = useInput('');
    const [outer, onChangeOuter ,setOuter] = useInput('');
    const [lookName, onChangeLookName ,setLookName] = useInput('');

    const {imagePaths, addPostDone, mainPosts} = useSelector((state) => state.post);

    const [previewImg, setPreviewImg] = useState(null);

    const router = useRouter();

    const [ content, onChangeContent, setContent ] = useInput('');

    useEffect(() => {

        if(addPostDone){
            Router.replace('/');
        }  
        
    }, [addPostDone]);

    useEffect(() => {
        dispatch({
            type: LOAD_POSTS_REQUEST
        });
    }, []);

    console.log("메인포스트: ", mainPosts);

    //게시글 업로드 요청
    const onSubmit = useCallback(() => {
        
        if(!content || !content.trim()){
            return alert('게시글을 작성하세요.');
        }
       
        const formData = new FormData();
        imagePaths.forEach((p) => {
            formData.append('image', p);
        });
        formData.append('content', content);
        formData.append('top', top);
        formData.append('bottom', bottom);
        formData.append('dress', dress);
        formData.append('shoes', shoes);
        formData.append('acc', acc);
        formData.append('outer', outer);
        formData.append('lookName', lookName);
        dispatch({
            type: ADD_POST_REQUEST,
            data: formData,
         });
    },[content, imagePaths]);

    const onClickImageUpload = useCallback(() => {
        imageInput.current.click();
    }, [imageInput.current]);


//이미지 변경, 이미지 미리보기
const onChangeImages = useCallback((e) => {
    console.log('images', e.target.files);
    const imageFormData = new FormData();
    [].forEach.call(e.target.files, (f) => {
        imageFormData.append('image', f);
    });
    
    const reader = new FileReader();
    if(e.target.files[0]){
      reader.readAsDataURL(e.target.files[0]);
    }
    reader.onloadend = () => {
      const previewImgUrl = reader.result;
      if(previewImgUrl){
        setPreviewImg(previewImgUrl);
      }
    }

   return dispatch({
        type: UPLOAD_IMAGES_REQUEST,
        data: imageFormData
    });
});
    

    const onRemoveImage = useCallback((index) => () => {
        dispatch({
            type: REMOVE_IMAGE,
            data: index
        });
    });

    return(
        <AppLayout >
            <div style={{width: 1120}}>
           <Row><h1>New DailyLook</h1> <h2 style={{marginLeft: 600, marginTop: 20}}>Recent DailyLook</h2></Row> 
            <Form encType="multipart/form-data" onFinish={onSubmit}>
                <Col style={{float: 'left'}}>
                    <Card
                    style={{
                    width: 280,
                    }}
                    
                    cover={
                        <img
                          alt="example"
                          src={previewImg ? previewImg : 'https://media.istockphoto.com/id/900575958/vector/dressmaker-model-vector-icon.jpg?s=612x612&w=0&k=20&c=7rnqVC3c7IqiWFrRkWPAZ34h-NAs-pkdsDGR5HYIv94='}
                          ref={imageInput}
                          height='528px'
                        />
                            }

                    actions={[
                        <Button onClick={onClickImageUpload} >
                            <UploadOutlined key="edit" />
                        </Button>
                        
                    ]}
                    >  
                      <input name="image" type="file" hidden ref={imageInput} onChange={onChangeImages} />
                    </Card>
                </Col>
            <Col style={{float: 'left', width:500}}>
            <div style={{ }}>
                <ul>
                <li style={{display: 'inline-block'}}><h3 >데일리룩 제목</h3></li>
                <li style={{display: 'inline-block', marginLeft:10}}><Input value={lookName} onChange={onChangeLookName} showCount maxLength={20} /></li>
                </ul>
                
                </div>
                <div>
                <Card style={{marginLeft: 15, marginTop: 0,width:400}}>
                <Button style={{backgroundColor: 'black', color: 'white', marginBottom: 10}} block >Information</Button>
    <thead>
        <tr >
        <th>분류</th>
        <th>브랜드명</th>
        
        </tr>
    </thead>
    <tbody style={{textAlign:'center'}} >
        <tr>
        <td> <Input disabled  defaultValue="상의" style={{color: 'black', textAlign:'center'}} /></td>
        <td> <Input value={top} onChange={onChangeTop} name="top" type="text" /></td> 
        </tr>

        <tr>
        <td> <Input disabled  defaultValue="하의" style={{color: 'black', textAlign:'center'}} /></td>
        <td> <Input value={bottom} onChange={onChangeBottom} type="text"  /></td> 
        </tr>

        <tr>
        <td> <Input disabled  defaultValue="원피스" style={{color: 'black', textAlign:'center'}} /></td>
        <td> <Input value={dress} onChange={onChangeDress} type="text" /></td> 
        </tr>

        <tr>
        <td> <Input disabled  defaultValue="아우터" style={{color: 'black', textAlign:'center'}} /></td>
        <td> <Input value={outer} onChange={onChangeOuter} type="text" /></td> 
        </tr>

        <tr>
        <td> <Input disabled  defaultValue="신발" style={{color: 'black', textAlign:'center'}} /></td>
        <td> <Input value={shoes} onChange={onChangeShoes}  type="text" /></td> 
        </tr>

        <tr>
        <td> <Input disabled  defaultValue="패션잡화" style={{color: 'black', textAlign:'center'}} /></td>
        <td> <Input value={acc} onChange={onChangeAcc}  type="text" /></td> 
        </tr>

    </tbody>
  </Card>
    </div>
    <Card style={{ marginTop: 25, marginLeft: 15, width: 400, height: 210}}>
    <Button style={{backgroundColor: 'black', color: 'white', marginBottom: 10}} block >Daily Record </Button>
    <Input.TextArea
    value={content}
    onChange={onChangeContent}
      showCount
      maxLength={250}
      style={{
        height: 110,
        resize: 'none',
      }}
    />
    </Card>

    <Button 
    style={{ left: '26%', marginTop: 5, backgroundColor: 'black', color: 'white'}}
    htmlType="submit"
    >
        데일리룩 등록하기
    </Button>

    </Col>
    </Form>  


        <div style={{ marginLeft: 720}}>
            {/* 가장 최근 데일리룩 */}
            <Carousel style={{marginRight: 0}} autoplay>
                <div>
                    <img style={{width: 300, height: 630}} src={`http://localhost:3065/${mainPosts[0].Images[0].src}`} />
                </div>
                <div>
                    <img style={{width: 300, height: 630}} src={`http://localhost:3065/${mainPosts[1].Images[0].src}`} />
                </div>
                <div>
                    <img style={{width: 300, height: 630}} src={`http://localhost:3065/${mainPosts[2].Images[0].src}`} />
                </div>
                <div>
                    { mainPosts[3] ? <img style={{width: 300, height: 630}} src={`http://localhost:3065/${mainPosts[3].Images[0].src}`} /> : null }
                </div>
            </Carousel>
        </div>
       
    

    


</div>
        </AppLayout>
    );
};




export default NewLook;
