import React, { createElement } from "react";
import { useSelector } from "react-redux";
import moment from "moment";
import Link from 'next/link';

const DateCellRender = () => {

   const {mainPosts} = useSelector((state) => state.post);

   console.log("DateCellRender:::::::::" + JSON.stringify(mainPosts));
   console.log("mainposts길이: ", mainPosts.length);
 //  console.log("Render:::::::::" + JSON.stringify(mainPosts[0].Images[0].src));

   const getDayInfo = () => {
      let calendarData;
      let diaryMoment;



      for( let i = 0; i < mainPosts.length; i++ ){
         console.log("생성 날짜",  moment(mainPosts[i].createdAt).format("YYYY-MM-DD"));
         diaryMoment = moment(mainPosts[i].createdAt).format("YYYY-MM-DD");

            // calendarData = [
            //    {
            //       photo: mainPosts[i].Images[0].src,
            //       title: mainPosts[i].lookName,
            //       date: diaryMoment,
            //       id: mainPosts[i].id
            //    }
            // ];

         if(diaryMoment === moment(mainPosts[i].createdAt).format("YYYY-MM-DD")){
          //  console.log("if문 실행");
            calendarData = [
               {
                  photo: mainPosts[i].Images[0].src,
                  title: mainPosts[i].lookName,
                  date: diaryMoment,
                  id: mainPosts[i].id
               },
            ];
         }
      }

      console.log("calendarData:::::" + JSON.stringify(calendarData));
      return calendarData || [];
   };

   const PhotoRander = ({photo}) => {

      switch(photo){
         case "photo" : 
         return React.createElement(
            <Link href="#">
                <img style={{width: 50}} src={`http://localhost:3065/${photo}`} />
            </Link>
         );
         default:
         return(
            <div></div>
         );   
      }

      // if(photo && title){
      //    return(
      //       <>
      //       <h5>{title}</h5>
      //       <Link href="#">
      //    <img style={{width: 50}} src={`http://localhost:3065/${photo}`} />
      //    </Link>
      //    </>
      //    );
      // }
      // else{
      //    return(
      //       <div></div>
      //    );
      // }

   };

   const NameRander = ({name}) => {

      switch(name){
         case "name" : 
         return(
            <h5>{name}</h5>
         );
         default:
         return(
            <div></div>
         );   
      }

   };

   const dayInfo = getDayInfo();

  //const photo = `http://localhost:3065/sean-kong-1dJmkdVuelg-unsplash_1669727155305.jpg`;

 return(
  <div>
   {dayInfo.map((info) => (

         <li key={info.id} >
            {/* <LookRander date={info.date} photo={info.photo} title={info.title} /> */}
             {/* <h5>{info.title}</h5>
            <Link href="#">
         <img style={{width: 50}} src={`http://localhost:3065/${info.photo}`} />
         </Link> */}
         <NameRander name={info.title} />
         <PhotoRander photo={info.photo} />
         </li>

      
   ))}
  </div>
 );
};

export default DateCellRender;
